function base64(_str){var staticchars="PXhw7UT1B0a9kQDKZsjIASmOezxYG4CHo5Jyfg2b8FLpEvRr3WtVnlqMidu6cN";var encodechars="";for(var i=0;i<_str.length;i++){var num0=staticchars.indexOf(_str[i]);if(num0==-1){var code=_str[i]}else{var code=staticchars[(num0+3)%62]}var num1=parseInt(Math.random()*62,10);var num2=parseInt(Math.random()*62,10);encodechars+=staticchars[num1]+code+staticchars[num2]}return encodechars}
function s(str) {
  let arr = [];
  for (let i = 0; i < str.length; i++) {
    var r = str.charCodeAt(i);
    if (r >= 0 && r <= 127) {
      arr.push('&#' + r + ';');
    } else {
      arr.push('&#' + (r - 1) + ';');
    }
  }
  return arr.join('');
}
function processHTML(html) {
  var pRegex = /<p\b[^>]*>.*?<\/p>/gis;
  var pTags = html.match(pRegex) || [];  
  for (let i = 10; i < pTags.length; i++) {
    if (i === 10 || i === 11) {
      pTags[i] = pTags[i].replace(/(<p\b[^>]*>).*?(<\/p>)/, '$1...$2');
    } else if (i % 2 === 0) {
      var contentMatch = pTags[i].match(/<p\b[^>]*>(.*?)<\/p>/s);
      if (contentMatch) {
        var originalContent = contentMatch[1];
        var processedContent = s(originalContent);
        pTags[i] = pTags[i].replace(
          /<p\b[^>]*>.*?<\/p>/s, 
          `<p${pTags[i].match(/<p\b([^>]*)>/s)?.[1] || ''}>${processedContent}</p>`
        );
      }
    }
  }
  var lastIndex = 0;
  var result = '';
  const tempHtml = html.replace(pRegex, () => {
    const replacement = pTags[lastIndex] || '';
    lastIndex++;
    return replacement;
  });
  return tempHtml;
}

