ruleToc.formatJs:
function formatChapter(index, title) {
  // Â¶ÇÊûú‰º†ÂÖ•ÁöÑÊòØÂ≠óÁ¨¶‰∏≤Êï∞Â≠óÔºàÂ¶Ç "23"ÔºâÔºåÂÖàËΩ¨Êàê Number
  const num = typeof index === 'string' ? parseInt(index, 10) : index;

  // Êï∞Â≠óËΩ¨‰∏≠Êñá
  const chineseNum = numberToChinese(num);

  // ÊãºÊé•Êàê "Á¨¨XÁ´† Ê†áÈ¢ò" Ê†ºÂºè
  return `Á¨¨${chineseNum}Á´† ${title.trim()}`;
}

/**
 * Êï∞Â≠óËΩ¨‰∏≠ÊñáÔºàÊîØÊåÅ 0~99999Ôºâ
 * @param {number} num - Ë¶ÅËΩ¨Êç¢ÁöÑÊï∞Â≠ó
 * @returns {string} ‰∏≠ÊñáÊï∞Â≠óÔºåÂ¶Ç "‰∏Ä"„ÄÅ"‰∫åÂçÅ‰∏â"„ÄÅ"‰∏ÄÁôæÈõ∂‰∫î"
 */
function numberToChinese(num) {
  const chineseNums = ["Èõ∂", "‰∏Ä", "‰∫å", "‰∏â", "Âõõ", "‰∫î", "ÂÖ≠", "‰∏É", "ÂÖ´", "‰πù", "ÂçÅ"];
  const units = ["", "ÂçÅ", "Áôæ", "ÂçÉ", "‰∏á"];

  // 0~10 Áõ¥Êé•ËøîÂõû
  if (num <= 10) return chineseNums[num];

  // 11~19 ÁâπÊÆäÂ§ÑÁêÜÔºàÂ¶Ç "ÂçÅ‰∏Ä"„ÄÅ"ÂçÅ‰∫î"Ôºâ
  if (num < 20) return "ÂçÅ" + (num % 10 === 0 ? "" : chineseNums[num % 10]);

  let result = "";
  const strNum = num.toString();

  // ‰ªéÈ´ò‰ΩçÂà∞‰Ωé‰ΩçÈÄê‰ΩçËΩ¨Êç¢
  for (let i = 0; i < strNum.length; i++) {
    const digit = parseInt(strNum[i], 10);
    const unit = units[strNum.length - i - 1];

    if (digit !== 0) {
      result += chineseNums[digit] + unit; // ÊãºÊé•Êï∞Â≠óÂíåÂçï‰ΩçÔºàÂ¶Ç "‰∏âÁôæ"Ôºâ
    } else if (result[result.length - 1] !== "Èõ∂") {
      result += "Èõ∂"; // Â§ÑÁêÜÈõ∂ÔºàÈÅøÂÖçÈáçÂ§çÔºâ
    }
  }

  // Ê∏ÖÁêÜÂ§ö‰ΩôÁöÑÈõ∂ÔºàÂ¶Ç "‰∏ÄÁôæÈõ∂" ‚Üí "‰∏ÄÁôæ"Ôºâ
  return result.replace(/Èõ∂+$/, "").replace(/Èõ∂+/g, "Èõ∂");
}
formatChapter(index, title)

