    return text.replace(pat, word => {
        var read = dict[word];
        if (/[„ÅÅ-„Çì„Ç°-„É≥]/.test(word) && /[‰∏Ä-ÈæØ„ÄÖ]/.test(word)) {
            var pre = (word.match(/^([„ÅÅ-„Çì„Ç°-„É≥]+)/) || [''])[0];
