function extractDomain(url) {
    try {
        const urlObj = new URL(url);
        const uParam = urlObj.searchParams.get('u');
        if (uParam) {
            const decodedUrl = decodeURIComponent(uParam);
            const domainMatch1 = decodedUrl.match(/https?:\/\/([^\/]+)/);
            if (domainMatch1 && domainMatch1[1]) {
                return domainMatch1[1];
            }
        }
    } catch (e) {
    }
    const domainMatch2 = url.match(/u=https?(?:%3A%2F%2F|:\/\/)([^\/&]+)/i);
    if (domainMatch2 && domainMatch2[1]) {
        return decodeURIComponent(domainMatch2[1]);
    }
    const fallbackMatch = url.match(/u=([^&]+)/);
    if (fallbackMatch && fallbackMatch[1]) {
        const decoded = decodeURIComponent(fallbackMatch[1]);
        const domainMatch3 = decoded.match(/https?:\/\/([^\/]+)/);
        if (domainMatch3 && domainMatch3[1]) {
            return domainMatch3[1];
        }       
        const parts = decoded.split('/');
        if (parts.length > 2) {
            return parts[2];
        }
    }
    return null;
}

function getHost(Url) {
    if (Url.startsWith('http://')) Url = Url.substring(7);
    if (Url.startsWith('https://')) Url = Url.substring(8);
    const firstSlashIndex = Url.indexOf('/');
    if (firstSlashIndex !== -1) {
        return Url.substring(0, firstSlashIndex);
    }
    return Url;
}

