var javaImport = new JavaImporter();
javaImport.importClass(
  Packages.android.util.Base64,
  Packages.java.lang.String,
  Packages.java.net.URL,
  Packages.okhttp3.HttpUrl
);
with (javaImport) {
  function btoa(data) {
    return Base64.encodeToString(String(data).getBytes("UTF-8"), 2);
  }

  function getSubDomain(url) {
    let baseUrl = getBaseUrl(url);
    if (!baseUrl) {
      return url;
    }
    try {
      let mURL = URL(baseUrl);
      let host = mURL.host;
      if (isIPAddress(host)) return host;
      return HttpUrl.parse(baseUrl).topPrivateDomain() || host;
    } catch (e) {
      this.java.log(e);
      return baseUrl;
    }
  }

  function getDomain(url) {
    let baseUrl = getBaseUrl(url);
    if (!baseUrl) {
      return url;
    }
    try {
      return URL(baseUrl).host;
    } catch (e) {
      return baseUrl;
    }
  }
  /**
   * ç§»é™¤cookie
   */
  function removeCookie(url) {
    const { cookie } = this;
    cookie.removeCookie(url);
    let domains = [getDomain(url), getSubDomain(url)];
    domains.forEach((domain) => {
      cookie.removeCookie(domain);
    });
  }
}

function getBaseUrl(url) {
  if (!url) {
    return null;
  }
  url = String(url);
  if (url.match(/https?:\/\//i)) {
    var index = url.indexOf("/", 9);
    return index == -1 ? url : url.substring(0, index);
  }
  return null;
}

function isIPv4Address(ip) {
  ip = String(ip);
  let parts = ip.split(".");
  if (parts.length !== 4) return false;

  for (let part of parts) {
    if (!/^\d+$/.test(part)) return false; // å¿…é¡»æ˜¯æ•°å­—
    if (part.length > 1 && part[0] === "0") return false; // ç¦æ­¢å‰å¯¼é›¶
    let num = parseInt(part, 10);
    if (num < 0 || num > 255) return false; // èŒƒå›´æ£€æŸ¥
  }
  return true;
}

function isIPv6Address(ip) {
  ip = String(ip);
  // å¤„ç†åŒå†’å·ï¼ˆæœ€å¤šå‡ºç°ä¸€æ¬¡ï¼‰
  if (ip.includes(":::")) return false;
  let doubleColonCount = (ip.match(/::/g) || []).length;
  if (doubleColonCount > 1) return false;

  // åˆ†å‰²æˆç»„
  let groups = ip.split(":");
  let validGroupCount = 8;
  let actualGroupCount = groups.filter((g) => g !== "").length;

  // éªŒè¯ç»„æ•°
  if (doubleColonCount === 1) {
    if (actualGroupCount > validGroupCount - 1) return false;
  } else {
    if (groups.length !== validGroupCount) return false;
  }

  // éªŒè¯æ¯ç»„å†…å®¹
  for (let group of groups) {
    if (group === "") continue; // è·³è¿‡ç©ºç»„ï¼ˆåŒå†’å·éƒ¨åˆ†ï¼‰
    if (!/^[0-9a-fA-F]{1,4}$/.test(group)) return false; // 1-4ä½åå…­è¿›åˆ¶
  }
  return true;
}

function isIPAddress(input) {
  return isIPv4Address(input) || isIPv6Address(input);
}

function getSessionId(cookieString) {
    const match = cookieString.match(/sessionid=([^;]+)/);
    return match ? match[1] : null;
}

function getKey(key) {
  let parts = key.split(";");
  for (let part of parts) {
    if (part.includes("qttoken")) {
      return part.split("=")[1];
    }
  }
  return "";
}

