function decryptData(Data, userkey) {
	 const { java } = this 
 try {
 const fullData = java.base64DecodeToByteArray(Data);
 const ivBytes = fullData.slice(0, 16);
 const ContentBytes = fullData.slice(16);

 const keyTime = 60000 * Math.floor(Date.now() / 60000);
 const keyTimes = [keyTime, keyTime - 60000, keyTime - 60000*2];
 //java.log(keyTimes.toString());

 for (let ks of keyTimes) {
 try {
 let keyHex = java.digestHex(userkey + ks, "SHA-256");
 let keyBytes = java.hexDecodeToByteArray(keyHex);

 let cipher = java.createSymmetricCrypto(
 "AES/CBC/PKCS7Padding",
 keyBytes,
 ivBytes
 );

 let content = cipher.decryptStr(ContentBytes);
 //java.log(content);
 if (content) {
