小小追书：
http://zsdqapp.net/xxzs/
点点阅读：
https://xxydapps.com/ddyd/
书香之家：
https://ttzsapp.com/sxzj/
追书大师：
https://zsdsapp6.com/zsds/
笔趣阁开心版：
https://bqgkxb.com/	
*/
//解密(by  尐哖 & landseer)
var javaImport = new JavaImporter();
javaImport.importPackage(
    Packages.java.lang,
    Packages.javax.crypto.spec,
    Packages.javax.crypto,
    Packages.java.util,
    Packages.java.io
);
with(javaImport) {
    function decode(str) {
        data=java.base64DecodeToByteArray(str);
        datas=Arrays.copyOfRange(data,16,data.length-16);
        k=String(Arrays.copyOfRange(data, 0, 16));
        k=java.digestHex(k,'sha-256');
        ks=[];
        length = k.length();
        if (length % 2 == 1) {
            length++;
            k = "0" + k;
        }
        i = 0;
        i2 = 0;
        while (i < length) {
            i3 = i + 2;
ks[i2] =intToByte( Integer.parseInt(k.substring(i, i3),16));
            i2++;
            i = i3;
        }
        i=String(Arrays.copyOfRange(data,data.length-16, data.length));
         v=java.md5Encode(i);
        bytes = i.getBytes();
        bytes2 = v.getBytes();
        ivs=[];
        for (i = 0; i < 16; i++) {
            ivs[i] = (bytes2[i] ^ bytes[i]) ^ (-1); 
        }
       key = SecretKeySpec(ks, "AES");  
       iv = IvParameterSpec(ivs);
        var chipher = Cipher.getInstance("AES/CBC/PKCS5Padding");
        chipher.init(2, key, iv);
        return String(chipher.doFinal(datas));
    }
}
function intToByte(i) {
        var b = i & 0xFF;
        var c = 0;
        if (b >= 128) {
            c = b % 128;
            c = -1 * (128 - c);
        } else {
            c = b;
        }
        return c;
    }

exploreUrl:
@js:
sort=[];
burl=source.getKey().replace('//s','//book');
push=(title,url,type1,type2)=>sort.push({
		title: title,
		url: url,
		style: {
				layout_flexGrow: type1,
				layout_flexBasisPercent: type2
			}
	});

	push("❤推荐", null, 1, 1);
tj = 	["每日优选","高分好书","真香新书","爆肝书单","宝藏好书","猜你喜欢"];
	tj.map((title,type)=>{
	url = burl + `/book_city/v7_more/index/1/20/${type+1}/{{page}}.html`
		push(title, url, 1, 0.25);										
			});


	eval(String(source.bookSourceComment));
	fl_url = burl + "/classify/v4/index.html";
	fl_content = JSON.parse(java.ajax(fl_url)).data.content;	
  fl = JSON.parse(decode(fl_content));

	[
	 ["❤男频-分类","1",fl.male],
		["❤女频-分类","2",fl.female],
	].map([title,gender,list]=>{		
		push(title, null, 1, 1);
			list.map(($,index)=>{	   	    

 	    ltype_name = $.ltype_name;
 	    ltype_id=$.ltype_id;
 	    ltype_url = burl + `/classify/v4/all/${gender}/${ltype_id}/-1/-1/-1/{{page}}.html`; 										
 	    push("@"+ltype_name+"@", ltype_url, 1, 1);

     	D=$.ltype_list;
   	D.map(($,index)=>{	   	     	    
   		 title = $.stype_name;
     	stype_id=$.stype_id;

     url = burl + `/classify/v4/all/${gender}/${ltype_id}/${stype_id}/-1/-1/{{page}}.html`; 											  
     	if(D.length > 3&&D.length!=4){
		if(index+1 <= D.length - D.length%3)
			 {	push(title, url, 1, 0.25);}
				else{ push(title, url, 0, 0.29);}
				}
				else if(D.length == 4){ push(title, url, 1, 0.4);}
				else{ push(title, url, 1, 0.25);}
        });  
     });
  });


JSON.stringify(sort);

