style:
*{
	margin:0;padding:0;
}
html{
	background-color:#222;
}
a{
	text-decoration:none;
	color:#222;
}
ul{
	list-style:none;
}
iframe{
	width:100%;height:100%;
}
h1,h2,h3,h4,h5,h6{
	font-size:100%;font-weight:normal;
}
img,video{
	height:auto;max-width:100%;
}
#MacPlayer{
	z-index:9999;
	width:100%;height:auto;
	position:fixed;top:0;left:0;
	background-color:black;
}
#MacPlayer iframe{
	width:100%;height:217px;
}
.url_text{
	overflow-x:hidden;
	width:100%;height:auto;
	color:#fff;background-color:black;
	font-size:10px;white-space: nowrap;
}

/*块通用*/ 
.card{padding:15px 15px 0 15px;}
.card .c_header{display: flex;flex-direction: row;justify-content: space-between;padding-bottom:20px;} 
.card .c_title{font-size:18px;color:#fff;font-weight:700;} 
.card .c_title a{color:#fff;} 
.card .c_right a{color:#666;} 
.card .clear_history{color:#FDD835;display:none;}

/*详情页资料卡*/ 
.v_info .v_title,.v_info .v_title{font-size:22px;color:#eee;line-height:26px;} .v_info .v_desc{position:relative;font-size:12px;color:#999;padding-top:4px;} .v_info .v_desc a{color:#999;} .v_info .v_desc .desc{color:#FF3D00;font-weight:700;} .v_info .hr{color:#666;padding:0 4px;} .v_info .open_content{position:absolute;bottom:0;right:20px;color:#FFB026;text-decoration: underline;padding-left:10px;} .v_info .v_content{display:flex;padding-top:20px;} .v_info .cover,.v_info img{width:auto;height:100px;background:#333;} .v_info .cover{margin-right:10px;} .v_info .content{height:101px;overflow:hidden;font-size:12px;flex:1;color:#999;line-height:19px;position: relative;} .v_info .show_more{position: absolute;bottom:0;right:0;width:100%;padding:38px 0 19px 0;height:19px;background: linear-gradient(to bottom, rgba(34,34,34,0) 0%,rgba(34,34,34,1) 100%);color:#eee;text-align:center;display:none;} .v_info .auto{height:auto;}

/*tab */ 
.tab_control{position:absolute;display: flex;flex-direction: row;overflow-x:scroll;width:92.3%} 
.tab_control li{flex:1;position:relative;height:30px;line-height:34px;top:4px;text-align:center;color:#666;cursor:pointer;overflow: hidden;} 
.tab_control li span{display: inline-block;min-width: 10px;padding: 2px 2px;font-size: 9px;font-weight: 700;line-height: 1;color: #fff;text-align: center;white-space: nowrap;vertical-align: middle;background-color:#FFB026;border-radius: 2px;}
.tab_control li.current{z-index:99;height:37px;font-weight:700;color:#eee;background-image:linear-gradient(180deg, #BF360C 0%, #333 90%);border-radius: 4px 4px 0 0;} 
.tab_content{padding:12px;background: #333;box-shadow: 0 2px 8px 0 rgba(0,0,0,0.50);border-radius: 5px;} 
.tab_content ul.current{display:block;} 

/*播放来源、地址、排序*/
	.v_list{margin-bottom:20px;} #play_list{position:relative;} #play_list ul.current{display:flex;} .play_from li{flex:none;padding:0 10px;} .play_list{display:none;flex-flow: wrap;margin-right:-10px;} .play_list li{width:20%;margin-bottom:10px;} .play_list li a{overflow:hidden;height:40px;line-height:40px;border-radius: 5px;display:block;background:#222;color:#999;text-align:center;margin-right:10px;} .play_list li a:visited{color:#444;} .play_list li.current a{border: 1px solid #BF360C;color:#BF360C;}

/*处理电影的集数宽度*/ 
.large li{width:25%;} .play_list_sort{padding-bottom:20px;color:#999;} 

/*遮罩*/ 
.mask{width:100%;height:100%;background:rgba(0, 0, 0, 0.5);position: fixed;left:0;top:0;display: none;} 

/*白天主题配色*/
@media(prefers-color-scheme:light){
html{background:#FEFEFE;}
.v_title{color:black !important;}
.v_desc,.v_desc a,#intro{color:#444 !important;}
.open_content{color:#4898E0 !important;}
#show_more{background: linear-gradient(to bottom,transparent,#FEFEFE 100%);}
.tab_content{background:#FEFEFE;}
.tab_control li span{background:#4898E0;}
.tab_control li.current{color:#eee;background: linear-gradient(to bottom,black 10%,white 90%);}
.play_list li a{color:#444;background:#eee;}
.play_list li.current a{color:black;border:2px solid black;}
}

