.dlb {
	animation: pulse 2s infinite;
	width: 33%;
	display: inline-block;
	color: white;
	padding: 18px 45px;
	font-size: 1.4rem;
	font-weight: 600;
	border-radius: 60px;
	text-decoration: none;
	box-shadow: 0 10px 25px rgba(0, 176, 155, 0.4);
	transition: all 0.3s ease;
	position: relative;
	margin: 30px 0;
	overflow: hidden;
	border: none;
	text-align: center;
	-webkit-tap-highlight-color: transparent;
}
.dlb:hover {
	transform: translateY(-5px);
	box-shadow: 0 15px 30px rgba(0, 176, 155, 0.6);
}
.dlb:active {
	transform: translateY(0);
}
.dlb::after {
	content: "";
	position: absolute;
	top: -50%; left: -60%;
	width: 20px; height: 200%;
	background: rgba(255, 255, 255, 0.3);
	transform: rotate(25deg);
	transition: all 0.6s;
}
.dlb:hover::after {
	left: 120%;
}
