${body}
${yht}
<script> 
function daoru(url,id,copy=false) {
	url = url.match(/\\/main\\/(.+)/)[1];
	var yy="";
	switch(id) {
		case "1": yy = "https://gitee.com/lyc486/yuedu2/raw/main/";break;
		case "2": yy = "https://yd.loyc.xyz/";break;
		case "3": yy = "https://raw.githubusercontent.com/Luoyacheng/yuedu/main/";break;
		case "4": yy = "https://cdn.jsdelivr.net/gh/Luoyacheng/yuedu@main/";break;}
yy += url;
if (copy) return yy;
window.location.href="legado://import/importonline?src="+yy;}

const js=document.getElementById('js');
const ts=document.getElementById('ts');
let Timer = null;
let Timer2 = null;
let pressTime = 0;
let timerInterval = null;
const btns = document.querySelectorAll('.btn');
btns.forEach(btn => {
btn.addEventListener('pointerdown', function () {
	const btnId = this.id.replace('btn', '');
	const url = btn.closest("tr").dataset.url;
	pressTime = 0;
	timerInterval = setInterval(() => {
		pressTime += 10;
		js.textContent = pressTime+"ms";
		}, 10);
		Timer = setTimeout(() => {
   copyToClipboard(daoru(url, btnId,true));
 }, 800);
});

btn.addEventListener('pointerup', function () {
clearTimeout(Timer);
clearInterval(timerInterval);
if (pressTime > 0 && pressTime < 800) {
const btnId = this.id.replace('btn', '');
const url = btn.closest("tr").dataset.url;
daoru(url, btnId);}
Timer2 = null;
Timer2 = setTimeout(() => {
ts.textContent = js.textContent = "";
 }, 900);
});

btn.addEventListener('pointercancel', function () {
clearTimeout(Timer);
clearInterval(timerInterval);
});

});

function copyToClipboard(text) {
navigator.clipboard.writeText(text).then(() => {
ts.textContent = "已复制订阅链接";
}).catch(err => {
 alert("复制失败: " + err);
});
}
</script>
${yhjs}
</body>
</html>`;}
