@js:
eval(String(source.loginUrl));
var exploreUrl = [];
var Sort = `{{get('sort',Get('o'))}}`;
var Year = `{{Map('年份：')}}`;
var Month = `{{Map('月份：')}}`;
function explore(e, f) {
    if (f=='收藏') {
        style = `${e}::/search?query=${e}&type=&genre=全部&sort=${Sort}&year=${Year}&month=${Month}&page={{page}}\n`;
    } else if (f=='排序') {
        style = `${e}::/search?query=&type=&genre=全部&sort=${e}&year=${Year}&month=${Month}&page={{page}}\n`;
    } else if (f=='分类') {
        style = `${e}::/search?genre=${e}&sort=${Sort}&year=${Year}&month=${Month}&page={{page}}\n`;
    } else {//标签
        style = `${e}::/search?query=&type=&genre=&sort=${Sort}&tags%5B%5D=${e}&year=${Year}&month=${Month}&page={{page}}\n`;
    }
    return style;
}

sc = Map('收藏：');
scs = sc !== '' ? sc.split(",") : [];
if (scs.length > 0) {
    scs.forEach((sc) => {
        exploreUrl.push(explore(sc, '收藏'));
    });
}

sorts = ['最新上市','最新上傳','本日排行','本週排行','本月排行','觀看次數','他們在看'];
for (i in sorts) {
    exploreUrl.push(explore(sorts[i], '排序'));
}

genres = ['裏番','泡麵番','Motion+Anime','3D動畫','同人作品','Cosplay'];
for (i in genres) {
    exploreUrl.push(explore(genres[i], '分类'));
}

J = org.jsoup.Jsoup.parse(java.ajax(String(source.getKey()).replace(/(.*\/\/[a-z0-9.-]+).*/, '$1')+'/search'));
tags = J.select('.checkmark');
for (i in tags) {
    exploreUrl.push(explore(tags[i].text(), '标签'));
}

