<!doctype html>
<html>
<head lang="zh">
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1, minimum-scale=1, maximum-scale=1, user-scalable=no">
<meta http-equiv="x-ua-compatible" content="ie=edge">
<link rel="icon" href="yuedu.svg">
<title>写源助手</title>
<style>
body {
  background-color: #f4f5f7;
  display: flex;
  flex-wrap: wrap;
  place-content: center;
  place-items: start;
}
.card {
  border-top: .5px solid rgba(255,255,255,.7);
  color: #001944;
  border-radius: 24px;
  background-color: rgba(255,255,255,.5);
  box-shadow: 0px 10px 20px rgba(0,0,0,.05);
  padding: 30px;
  margin: 12.5px;
  width: 250px;
}
.flex {
  display: flex;
  flex-wrap: wrap;
}
details + div {
  overflow: hidden;
  max-height: 0;
  opacity: 0;
  transition: .5s;
}
[open] + div {
  max-height: 888px;
  opacity: 1;
}
summary {
  font-size: 23px;
  font-weight: 700;
  user-select: none;
  outline: 0;
  opacity: .45;
  transition: .5s;
  display: block;
}
[open] summary {
  opacity: 1;
  margin-bottom: 7px;
  font-size: 20px;
}
a {
  margin-top: 7px;
  display: inline-block;
  span { margin-left: 7px; }
}
span {
  background-color: rgba(0,0,0,.04);
  font-family: monospace;
  font-size: 13px;
  padding: .125em .25em;
  border-radius: .5em;
  opacity: .8;
}
button {
  font-size: 13px;
  font-weight: 700;
  color: #4360b7;
  background-color: #e7eeff;
  padding: 6.5px 13px;
  margin-top: 7px;
  margin-right: 7px;
  border-radius: 28px;
  border:none;
  transition: .3s;
}
button:active { background-color: #d6ddee; }
textarea {
  margin-top: 7px;
  padding: 7px;
  width: 232px;
  min-height: 72px;
  max-height: 143px;
  display: block;
  resize: vertical;
  font-size: 13px;
  border-radius: 8px;
  color: #333;
  border: 2px solid rgba(0,0,0,.1);
  background-color: rgba(0,0,0,.01);
}
#matchText,
#replaceText {
  resize: none;
  min-height: 17px;
  max-height: 17px;
}
input {
  appearance: none;
  vertical-align: middle;
  margin-right: 3px;
  margin-bottom: 6px;
  width: 15px;
  height: 15px;
  background-color: rgba(0,0,0,.01);
  border: 2px solid rgba(0,0,0,.1);
  border-radius: 8px;
  transition: .3s;
}
input:checked { border-color: #4360b7; }
label {
  display: inline-block;
  margin-top: 10px;
  font-size: 15px;
}
#tip { margin-top: 7px; }
#temp { display: none; }
</style>
</head>
<body>
<div class="card">
  <details>
    <summary>发现格式工具</summary>
  </details>
  <div>
    <textarea id="text" placeholder="请输入要处理的内容"></textarea>
    <a>格式转换</a>
    <div class="flex">
      <button onclick="Format()">html转旧发现</button>
      <button onclick="Switch()">新旧发现互转</button>
    </div>
    <a>每行列数<span>仅新发现</span></a>
    <div class="flex">
      <button onclick="Add(1)">1</button>
      <button onclick="Add(0.4)">2</button>
      <button onclick="Add(0.25)">3</button>
      <button onclick="Add(0.2)">4</button>
      <button onclick="Add(0.15)">5</button>
    </div>
    <a>快捷功能</a>
    <div class="flex">
      <button onclick="Copy('#text')">复制全文</button>
      <button onclick="Cut('#text')">剪切全文</button>
      <button onclick="Empty('#text')">清空</button>
    </div>
  </div>
</div>
<div class="card">
  <details>
    <summary>正则匹配替换</summary>
  </details>
  <div>
    <textarea id="text1" placeholder="请输入要处理的内容"></textarea>
    <a>正则匹配</a>
    <textarea id="matchText" placeholder="正则表达式"></textarea>
    <label>
      <input id="global" type="checkbox" checked>
      全局搜索
    </label>
    <label>
      <input id="ignoreCase" type="checkbox">
      忽略大小写
    </label>
    <div class="flex">
      <button onclick="Match()">进行匹配</button>
      <button onclick="Copy('#matchResult')">复制结果</button>
      <button onclick="Reset()">清空</button>
    </div>
    <div id="tip"></div>
    <textarea id="matchResult" placeholder="匹配结果"></textarea>
    <a>正则替换</a>
    <textarea id="replaceText" placeholder="想要替换成什么"></textarea>
    <div class="flex">
      <button onclick="Add1('{\{page}}')">{&#123;page}}</button>
      <button onclick="Add1('\\')">&#92;</button>
      <button onclick="Add1('&#60;')">&#60;</button>
      <button onclick="Add1('&#62;')">&#62;</button>
    </div>
    <div class="flex">
      <button onclick="Replace()">进行替换</button>
      <button onclick="Copy('#replaceResult')">复制结果</button>
    </div>
    <textarea id="replaceResult" placeholder="替换结果"></textarea>
  </div>
</div>
<div class="card">
  <details>
    <summary>编码格式转换</summary>
  </details>
  <div>
    <textarea id="text2" placeholder="请输入要处理的内容"></textarea>
    <a>url编码</a>
    <label>
      <input id="ignoreComponent" type="checkbox">
      编码<span>@#$&=:/,;?+</span>
    </label>
    <div class="flex">
      <button onclick="Encode()">进行编码</button>
      <button onclick="Decode()">进行解码</button>
    </div>
    <a>html转义</a>
    <label>
      <input id="super" type="checkbox">
      转义全部字符
    </label>
    <div id="temp"></div>
    <div class="flex">
      <button onclick="EncodeHtml()">进行转义</button>
      <button onclick="DecodeHtml()">进行反转义</button>
    </div>
    <a>快捷功能</a>
    <div class="flex">
      <button onclick="Copy('#text2')">复制全文</button>
      <button onclick="Cut('#text2')">剪切全文</button>
      <button onclick="Empty('#text2')">清空</button>
    </div>
  </div>
</div>
<div class="card">
  <details>
    <summary>外部在线工具</summary>
  </details>
  <div>
    <div class="flex">
      <button onclick="window.location.href='https://www.iamwawa.cn/jianfanti.html'">简繁转换</button>
      <button onclick="window.location.href='https://www.yuque.com/legado/yuan'">写源教程</button>
    </div>
  </div>
</div>
<script>
function $(t) {
  return document.querySelector(t);
}
function $$(t) {
  document.execCommand(t);
}
function Format() {
  t = $('#text');
  list = t.value.match(/<[^<]+?href=['"](.*?)['"][^<]*?>(.*?)</g);
  if (list) {
    html = "";
    for (i = 0; i < list.length; i++) {
      a = list[i].match(/<[^<]+?href=['"](.*?)['"][^<]*?>(.*?)</g);
      html += a[2] + "::" + a[1] + "\r\n"
    };
    t.value = html.replace(/\r\n$/, "");
  }
}
function Switch() {
  t = $('#text');
  try {
    html = "";
    json = JSON.parse(t.value);
    for (i = 0; i < json.length; i++) {
      html += json[i].title + "::" + json[i].url + "\r\n"
    };
    t.value = html.replace(/\r\n$/, '');
  } catch(err) {
    if (t.value.match(/\&\&|::/g)) {
      text = t.value.replace(/\&\&/g, '\r\n');
      list = text.match(/.*?::.*/g);
      html = [];
      for (i = 0; i < list.length; i++) {
        a = list[i].match(/(.*?)::(.*)/);
        html.push({
          title: a[1],
          url: a[2],
        });
      };
      t.value = JSON.stringify(html);
    }
  }
}
function Add(t) {
  x = $('#text');
  try {
    json = JSON.parse(x.value);
    for (i = 0; i < json.length; i++) {
      json[i].style = {
        layout_flexGrow: 1,
        layout_flexBasisPercent: t,
      }
    };
    x.value = JSON.stringify(json);
  } catch(err) {
    "";
  }
}
function Copy(t) {
  $(t).select();
  $$('copy');
}
function Cut(t) {
  $(t).select();
  $$('cut');
}
function Empty(t) {
  $(t).value = "";
  $(t).focus();
}
function Reset() {
  $('#text1').value = $('#matchText').value = $('#tip').textContent = $('#matchResult').value = $('#replaceText').value = $('#replaceResult').value = "";
  $('#text1').focus();
}
function BuildRegex() {
  text = $('#text1');
  if (!text.value) {
    text.focus();
    return false;
  }
  match = $('#matchText');
  if (!match.value) {
    match.focus();
    return false;
  }
  op = $('#global').checked ? "g": "";
  op = $('#ignoreCase').checked ? op + "i": op;
  return new RegExp(match.value, op);
}
function Match() {
  regex = BuildRegex();
  t = $('#tip');
  x = $('#matchResult');
  tip = text = t.textContent = x.value = "";
  match = $('#text1').value.match(regex);
  if ($('#global').checked) {
    for (i = 0; i < match.length; i++) {
      text += match[i] + "\r\n";
    };
    tip = "共找到 " + match.length + " 处匹配：\r\n";
    text = text.replace(/\r\n$/, '');
  } else {
    tip = "匹配位置：" + regex.lastIndex;
    text = match[0];
  }
  t.textContent = tip;
  x.value = text;
  return true;
}
function Add1(t) {
  x = $('#replaceText');
  start = x.selectionStart;
  length = x.selectionEnd - start;
  json = x.value.split('');
  json.splice(start, length, t);
  x.value = json.join('');
  x.focus();
  x.selectionStart = t.length + start;
  x.selectionEnd = t.length + start;
}
function Replace() {
  regex = BuildRegex();
  $('#replaceResult').value = $('#text1').value.replace(regex, $('#replaceText').value);
}
function Encode() {
  t = $('#text2');
  text = t.value;
  t.value = $('#ignoreComponent').checked ? encodeURIComponent(text): encodeURI(text);
}
function Decode() {
  t = $('#text2');
  text = t.value;
  t.value = decodeURIComponent(text);
}
function EncodeHtml() {
  t = $('#text2');
  text = t.value;
  if ($('#super').checked) {
    u = "";
    for (i = 0; i < text.length; i++){
      u += "&#" + text.charCodeAt(i) + ";";
    };
  } else {
    x = $('#temp');
    x.textContent = text;
    u = x.innerHTML
    .replace(/"/g, '&quot;');
  }
  t.value = u;
}
function DecodeHtml() {
  t = $('#text2');
  text = t.value;
  x = $('#temp');
  x.innerHTML = text
  .replace(/<br>/g, '\r\n')
  .replace(/</g, '&#60;')
  .replace(/>/g, '&#62;');
  t.value = x.textContent;
}
</script>
</body>
</html>
