            if (url.startsWith('data:')) url = atob(url.match(/^[^,]+,([^\s,]+)/)[1]);
            else if (/^[\da-z\+\/]{2,}=*$/i.test(url)) url = atob(url);
            setTimeout(() => {
                btn.classList.remove('an-s');
