<!DOCTYPE html>
<html>
<head>
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <style>
        body {
            margin: 0;
            background: white;
            overflow-x: hidden;
        }
        #videoContainer {
            width: 100%;
            position: relative;
            padding-top: 56.25%;
            background: #000;
        }
        #video {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
        }
        #seekInfo {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            background: rgba(0,0,0,0.7);
            color: white;
            padding: 10px 20px;
            border-radius: 5px;
            display: none;
            font-size: 18px;
            pointer-events: none;
        }
        .controls {
            position: absolute;
            bottom: 0;
            width: 100%;
            background: rgba(0,0,0,0.7);
            padding: 10px;
            box-sizing: border-box;
            opacity: 1;
            transition: opacity 0.3s;
        }
        #progressBar {
            height: 8px;
            background: #555;
            margin-bottom: 10px;
            cursor: pointer;
        }
        #progress {
            height: 100%;
            background: #2196F3;
            width: 0;
            transition: width 0.2s;
        }
        button {
            font-size: 14px;
            padding: 8px 15px;
            margin: 0 5px;
            border: none;
            border-radius: 3px;
        }
        #fullEpisode {
            background: blue;
            color: black;
            padding: 12px 25px;
            margin: 15px 0 15px 15px;
            display: inline-block;
        }
        .time {
            color: white;
            font-size: 14px;
            margin: 0 10px;
        }
        :-webkit-full-screen #videoContainer {
            width: 100% !important;
            height: 100% !important;
            padding-top: 0;
        }
    </style>
</head>
<body>
    <div id="videoContainer">
        <video id="video" src="你的播放链接"></video>
        <div id="seekInfo">[⏩︎ 00:00 / +0s]</div>
        <div class="controls">
            <div id="progressBar">
                <div id="progress"></div>
            </div>
            <div style="display: flex; align-items: center; justify-content: space-between;">
                <button id="playBtn">播放</button>
                <span class="time" id="currentTime">00:00</span>
                <span class="time" id="duration">00:00</span>
                <button id="fullscreenBtn">全屏</button>
            </div>
        </div>
    </div>
    <button id="fullEpisode">全一集</button>

    <script>
    //替换成播放链接
        const p = "{{result}}";

        const video = document.getElementById('video');
        const seekInfo = document.getElementById('seekInfo');
        const videoContainer = document.getElementById('videoContainer');
        video.src = p;

        let isScrubbing = false;
        let startX = 0;
        let controlsTimeout;

        function formatTime(seconds) {
            const min = Math.floor(seconds / 60);
            const sec = Math.floor(seconds % 60);
            return `${min}:${sec.toString().padStart(2, '0')}`;
        }

        // 播放控制
        document.getElementById('playBtn').addEventListener('click', () => {
            video[video.paused ? 'play' : 'pause']();
            playBtn.textContent = video.paused ? '播放' : '暂停';
        });

        // 进度更新
        video.addEventListener('timeupdate', () => {
            document.getElementById('progress').style.width = 
                (video.currentTime / video.duration * 100) + '%';
            document.getElementById('currentTime').textContent = 
                formatTime(video.currentTime);
        });

        // 进度跳转
        document.getElementById('progressBar').addEventListener('click', (e) => {
            const rect = e.target.getBoundingClientRect();
            const pos = (e.clientX - rect.left) / rect.width;
            video.currentTime = pos * video.duration;
        });

        // 全屏控制
        document.getElementById('fullscreenBtn').addEventListener('click', () => {
            if (!document.fullscreenElement) {
                videoContainer.requestFullscreen();
            } else {
                document.exitFullscreen();
            }
        });

        // 智能触摸控制
        videoContainer.addEventListener('touchstart', (e) => {
            startX = e.touches[0].clientX;
            isScrubbing = false;
            seekInfo.style.display = 'block';
        });

        videoContainer.addEventListener('touchmove', (e) => {
            const currentX = e.touches[0].clientX;
            const diff = currentX - startX;

            if (Math.abs(diff) > 5) { // 5px防误触阈值
                isScrubbing = true;
                const seekSeconds = Math.floor(diff / 10);
                const newTime = video.currentTime + seekSeconds;
                seekInfo.textContent = `${seekSeconds > 0 ? '⏩︎' : '⏪︎'} ${formatTime(newTime)} / ${Math.abs(seekSeconds)}s`;
                e.preventDefault(); // 阻止默认滚动行为
            }
        });

        videoContainer.addEventListener('touchend', (e) => {
            if (isScrubbing) {
                const endX = e.changedTouches[0].clientX;
                const diff = endX - startX;
                const seekSeconds = Math.floor(diff / 10);
                video.currentTime += seekSeconds;
            } else {
                toggleControls();
            }
            seekInfo.style.display = 'none';
            isScrubbing = false;
        });

        // 点击控制栏显示
        function toggleControls() {
            const controls = document.querySelector('.controls');
            controls.style.opacity = controls.style.opacity === '0' ? '1' : '0';
            clearTimeout(controlsTimeout);
            if (controls.style.opacity === '1') {
                controlsTimeout = setTimeout(() => controls.style.opacity = '0', 3000);
            }
        }

        // 单击控制（非触摸操作）
        videoContainer.addEventListener('click', (e) => {
            if (e.pointerType === 'mouse') { // 仅处理鼠标点击
                toggleControls();
            }
        });

        // 全一集按钮
        document.getElementById('fullEpisode').addEventListener('click', () => {
            video[video.paused ? 'play' : 'pause']();
        });

        // 初始化
        video.addEventListener('loadedmetadata', () => {
            document.getElementById('duration').textContent = formatTime(video.duration);
        });
    </script>
</body>
</html>

