@js:(function(result) {
    try {
        // 1. 解析页面源码（遵循Legado Jsoup调用规范，兼容执行环境）
        var doc = org.jsoup.Jsoup.parse(result);
        // 2. 定位当前页码：从.page_info1内.page_current1标签提取（标识当前页）
        var currentPageElem = doc.select("div.page_info1 a.page_current1").first();
        var currentPage = 0;

        if (currentPageElem) {
            // 3. 提取并转换当前页码（容错非数字文本，避免解析异常）
            var currentPageText = currentPageElem.text().trim() || "0";
            currentPage = /^\d+$/.test(currentPageText) ? parseInt(currentPageText, 10) : 0;
        }

        // 4. 定位下一页链接：匹配.page_info1内title为“下一页”的.page_link1标签
        var nextLinkElem = doc.select("div.page_info1 a.page_link1[title='下一页']").first();
        var nextUrl = "";

        // 5. 按需求判断：页码≤10返回链接，否则返回空（空值触发Legado停止加载）
        if (nextLinkElem && currentPage <= 10) {
            nextUrl = nextLinkElem.attr("href").trim() || "";
        }

        // 6. 返回结果：符合规则则返回链接，否则空值兜底
        return nextUrl;
    } catch (e) {
        // 7. 异常处理：避免脚本报错导致目录加载中断，统一返回空值
        return "";
    }
})(result)

