ruleSearch.intro:
@js:
const doc = org.jsoup.Jsoup.parse(result);
const list = doc.select(".play_list li").toArray();
for (let i = 0; i < list.length; i++) {
    let item = list[i];
    let title = item.select(".url").text().trim();
    let size = item.select(".size").text().trim();
    list[i] = `${i + 1}. ${title}  ${size}`;
}

list.join("\n");

