try {
	data = JSON.parse(source.getVariable());
} catch (err) {
	data = {};
}

reSearch = false;
if (baseUrl != _baseUrl&&data.url != _baseUrl) {
		reSearch = true;
		data.url = _baseUrl;
		source.setVariable(JSON.stringify(data));
		java.initUrl();
}
url = data.url || _baseUrl;
if (nologinflag) {
	url = String(url + "," + JSON.stringify({
		"method": "POST",
		"body": "action=1&v=1234"
	}));
