ruleContent.content:
@js:
if (baseUrl.match(/video/)){
	 var u = src.match(/url: '(.*m3u8)'/)[1]
	 var pi="✅播放ing";
	 //java.log(pi)
	 var b64 = java.base64Encode(
`<html>
    <head>
        <meta name="viewport" content="width=device-width">
        <style>
            body {
                margin: 0;
                font-family: Arial, sans-serif;
                background: linear-gradient(135deg, #ece9e6, #ffffff);
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: flex-start;
                padding: 20px;
            }
            .container {
                width: 100%;
                max-width: 800px;
                background: #fff;
                border-radius: 10px;
                box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
                overflow: hidden;
                margin-bottom: 20px;
            }
            h3 {
                margin: 0;
                padding: 10px 20px;
                font-size: 0.9em;
                background: #333;
                color: #fff;
                border-radius: 10px 10px 0 0;
            }
            .video-container {
                width: 100%;
                background: #000;
                position: relative;
            }
            video {
                width: 100%;
                height: auto;
                display: block;
            }
            .info-container {
                padding: 20px;
            }
            .info-container p {
                margin: 10px 0;
                color: #555;
            }
            .info-container p span {
                font-weight: bold;
                color: #000;
            }
        </style>
    </head>
    <body>
        <div class="container">
        <h3>${pi}</h3>
            <div class="video-container">
                <video controls autoplay name="media" id="videoPlayer">
                    <source src="${u}" type="video/mp4">
                </video>
            </div>
            <div class="info-container">

            </div>
        </div>

        <script>
            // 获取视频元素
            const video = document.getElementById('videoPlayer');
            let touchStartX = 0;
            let touchStartY = 0;
            let touchStartTime = 0;
            let isLongPress = false;

            // 滑动快进
            video.addEventListener('touchstart', (e) => {
                touchStartX = e.touches[0].clientX;
                touchStartY = e.touches[0].clientY;
                touchStartTime = Date.now();
                isLongPress = false;
            });

            video.addEventListener('touchmove', (e) => {
                const deltaX = e.touches[0].clientX - touchStartX;
                const deltaY = e.touches[0].clientY - touchStartY;

                // 如果是水平滑动，且未进入长按状态
                if (Math.abs(deltaX) > Math.abs(deltaY) && !isLongPress) {
                    const duration = video.duration;
                    const seekTime = video.currentTime + (deltaX / 10); // 每滑动100px快进1秒
                    video.currentTime = Math.min(Math.max(seekTime, 0), duration);
                }
            });

            // 长按快进
            video.addEventListener('touchend', (e) => {
                const touchEndTime = Date.now();
                const touchDuration = touchEndTime - touchStartTime;

                // 如果长按时间超过500ms，进入长按快进状态
                if (touchDuration > 500) {
                    isLongPress = true;

                    // 在长按状态下，根据滑动距离快进
                    const deltaX = e.changedTouches[0].clientX - touchStartX;
                    const duration = video.duration;
                    const seekTime = video.currentTime + (deltaX / 100); // 每滑动100px快进1秒
                    video.currentTime = Math.min(Math.max(seekTime, 0), duration);
                } else {
                    isLongPress = false;
                }
            });

            // 防止默认行为（如页面滚动）
            video.addEventListener('touchmove', (e) => {
                e.preventDefault();
            });
        </script>
    </body>
</html>`)
	 dataUrl = 'data:text/html;base64,' + b64;
	 	java.startBrowser(dataUrl,title);
	 	java.toast('正在加载视频...');
	 	result = ' \n\n【刷新正文】跳转播放\n不要相信且不要点击视频里的任何广告，误触后请立即返回\n🔗直链:\n' + u
	}else if(baseUrl.match(/cartoon/)){
	result = java.getString('.manhua@html')
	}else{
	result = java.getString('.xiaoshuo@html')
	}

