function base64(_str){
	var staticchars="PXhw7UT1B0a9kQDKZsjIASmOezxYG4CHo5Jyfg2b8FLpEvRr3WtVnlqMidu6cN";
	var encodechars="";
	for(var i=0;i<_str.length;i++){
