exploreUrl:
@js:
var result = [];
java.toast("🔥正在刷新列表,请稍等！");
var push = (title, url, size) => result.push({
    "title": title,
    "url": url,
    "style": {
        "layout_flexGrow": 1,
        "layout_flexBasisPercent": size
    }
});
var baseUrl = "https://m.hjbook.com/sort.html";
var tag = java.ajax(baseUrl);
if (tag) {
    var a = org.jsoup.Jsoup.parse(tag).select(".content a");
    push("🔥分类小说🔥", null, 1);
    for (var i in a) {
        var title = a[i].text();
        var url = a[i].attr("href");
        var size = 0.33;
        title = String(title).replace(/\s/g, "");
        url = String(url)+ "?page={{page}}";      
        push(title, url, size);
  }

} else {
  java.toast("🤔列表刷新失败！！！");     
} 
JSON.stringify(result);

