ruleContent.content:
@js:
result=JSON.parse(result)
const config=getConfig()
const resultList=[]
for (let ip = 0; ip < result.paragraphs.length; ip++) {
  let p = result.paragraphs[ip]
  if (/^<图片>http.+$/.test(p)) {
    p = '<img src="'+p.replace(/^<图片>/i, "")+'">'
    resultList.push(p)
    continue
  }
  function generateTran(i) {
    const chars = {
  	    baiduParagraphs: "ᵇ",
  	    youdaoParagraphs: "ʸ",
  	    gptParagraphs: "ᵍ",
  	    sakuraParagraphs: "ˢ"
    }
    if (config.translation==="并列") {
      const processed=[]
      for (let ii = 0; ii < config.translationArr.length; ii++) {
        let t = config.translationArr[ii]
        if (result[t]) {
          if (/^<图片>http.+$/.test(result[t][i])) {
            let tmp = '<img src="'+result[t][i].replace(/^<图片>/i, "")+'">'
            if (!processed.includes(tmp))
              processed.push(tmp)
            	continue
          }
          processed.push((result[t][i]!="" ? chars[t] : "") + result[t][i].trim())
          continue
        }
        processed.push("")
      }
      return processed.join("\n")
    } else {
      for (let tt=0; tt<config.translationArr.length; tt++) {
        let t=config.translationArr[tt]
        if (result[t]) {
          if (/^<图片>http.+$/.test(result[t][i])) {
            let tmp = '<img src="'+result[t][i].replace(/^<图片>/i, "")+'">'
            return tmp
          }
          return (result[t][i]!="" ? chars[t] : "") + result[t][i].trim()
        }
      }
    }
  	}
  switch (String(config.language)) {
  	  case "日文":
  	    resultList.push(p)
  	    break
  	  case "中文":
  	    resultList.push(generateTran(ip))
  	    break
  	  case "中日":
  	    resultList.push([generateTran(ip), p.trim()].join("\n"))
  	    break
  	  case "日中":
  	    resultList.push([p, generateTran(ip)].join("\n"))
  	    break
  	}
}
resultList.join("\n")

