if (/category/.test(result.url())) {
    const originUrl = java.ruleUrl;
    const filterID = [0, 5];
    let nextPage = page === 1 ? 2 : +source.getVariable() + 1;
    let Data = JSON.parse(result.body());
    let maxPage = Math.ceil(Data.data.page_info.count / 20);
    let bookList = Data?.data?.list?.filter(item => !filterID.includes(item.sid)) || [];

    /*
    	*过滤后数据为空时，翻页请求获取新数据
     *限制单次最多五次翻页，避免陷入死循环
     */
    let retryCount = 0;
    let currentUrl = originUrl;
    while (bookList.length === 0 && retryCount < 5 && nextPage <= maxPage) {
        currentUrl = String(currentUrl).replace(/\\"page\\":\\"(\d+)\\"/g, (match, pageNum) => {
            return `\\"page\\":\\"${+pageNum + 1}\\"`;
        });
        bookList = JSON.parse(java.ajax(currentUrl))?.data?.list?.filter(item => !filterID.includes(item.sid)) || [];
        retryCount++;
        nextPage++;
        if (bookList.length > 0) {
            break;
        }
    }
    //java.log(nextPage);
    // 利用源变量代替page变量作为页码
    source.setVariable(nextPage);
    result = Packages.io.legado.app.help.http.StrResponse(result.url(), JSON.stringify(bookList));
}

result;

