/**
 * 通用日志代理创建函数
 * @param {object} target - 被代理的目标对象
 * @param {string} name - 在日志中显示的代理名称
 * @returns {Proxy} - 返回一个带日志记录功能的代理对象
 */
function createLoggingProxy(target, name) {
  const { java } = this;
  return new Proxy(target, {
    get(target, prop, receiver) {
      //java.log(`${name}`, prop);
      return Reflect.get(target, prop, receiver);
    },
    set(target, prop, value) {
      //java.log(`set ${name}`, prop, value);
      return Reflect.set(target, prop, value);
    },
  });
};

/**
 * 模拟 ClassList 对象的工厂函数
 * @returns {Proxy}
 */
function createClassList() {
    const classList = {
        add(...args) {
            // java.log("classList add", args);
        },
    };
    return createLoggingProxy(classList, "classList");
};


/**
 * 通用模拟元素构造工厂函数
 * @param {string} tagName - 元素的标签名 (e.g., 'P', 'STYLE')
 * @param {object} [properties={}] - 附加到元素上的额外属性和方法
 * @param {string} [logName] - 日志记录中使用的名称，默认为 tagName 的小写
 * @returns {Proxy} - 返回一个模拟的 DOM 元素代理
 */
function createMockElement(tagName, properties, logName) {
  // 手动处理默认参数，以兼容不支持默认参数语法的旧引擎
  properties = properties || {};

  const upperTagName = tagName.toUpperCase();
  const baseElement = {
    nodeType: 1,
    tagName: upperTagName
  };

  Object.assign(baseElement, properties);

  return createLoggingProxy(baseElement, logName || tagName.toLowerCase());
};

/**
 * 专门用于创建 P 标签代理的函数，整合了所有 P 标签的共有行为
 * @param {string} pOuterHTML - P 标签的 outerHTML 字符串
 * @returns {Proxy}
 */
function createParagraphProxy(pOuterHTML) {
    const innerHTML = pOuterHTML.replace(/<\/?p>/g, "");
    return createMockElement('P', {
        innerHTML: innerHTML,
        classList: createClassList(),
        get outerHTML() {
            return `<p>${this.innerHTML}</p >`;
        },
    }, 'paragraph');
};

