function parseCategory(data) {
  const [title, url, num = defaultNumPerRow] = data.split(separator);
  const fullUrl = /^\d+|natural|wlhxca/.test(url) 
    ? `${baseApi}bookstore/getFullPage?channelBody=%7B%22${url}%22%3A%7B%22offset%22%3A%22{{(page-1)*25}}%22%2C%22limit%22%3A%2225%22%7D%7D&versionCode=148`
    : url;
  return [title, fullUrl, num];
}

function parseCategoryText(data){
  let cateTypeNum = data.split(",");
  let cateList = [];
  for(let i in cateTypeNum){
    let name = categories[cateTypeNum[i]];
    let list = cateNum[cateTypeNum[i]];
    for(let j in cateTag){   
      switch (Number(j)) {
        case 0:
        case 1:{                       
            list[0].length?cateList.push(`${分类标识}${name}${cateTag[j]}::::1`):null;             
            for(let k in list[0]){           
              cateList.push(`${list[0][k]}::${list[Number(j)+1][k]}`);
            }
            break;
        }
        case 2:{
            cateList.push(`${榜单标识}${name}${cateTag[j]}::::1`);
            for(k in cateType){
              list[Number(j)+1][k]?cateList.push(`${cateType[k]}::${list[Number(j)+1][k]}`):null;
            }
            break;
        }
      }
    }
  }
  return cateList;
}

function parseTag(data, tagCategory) {
  const [title, url, num = defaultNumPerRow, tagType] = data.split(separator);
  let fullUrl = url;
  let params =  `search/getSearchForKeyWords?versionCode=313&offset={{(page-1)*20}}&limit=20&removetags=0&fw=0&yc=0&xx=${tagCategory}&sd=0&fg=0&mainview=0&fbsj=0&collectionTypes=ors&isfinish=0&sortType=0&token={\{Map("token")}}`;
  if (tagType === "bq") {
    fullUrl = `${baseApi}${params}&bq=${url}`;
  } else if (tagType === "at") {
  	fullUrl = `${baseApi}androidapi/authorColumn?authorid=${url}`;
  	}else if (tagType === "class") {
  		let nn = url.split(",");
  	fullUrl = `${baseApi}androidapi/incrementFavorite,{"method":"POST","body":"versionCode=313&token=${token}&order={\{java.put("order",${nn[2]})}}&classId=${nn[0]}&backupTime=${Date.now()}"}`
  	}else if (tagType === "lx") {
    fullUrl = `${baseApi}${params}&lx=${url}`
  }
  return [title, fullUrl, num];
}

function createFlexBox(title, url, num) {
  const flexBasisMap = { 1: 1, 2: 0.4, 3: 0.25, 4: 0.2, 5: 0.15, 7: 0.1, 10: 0.05 };
  return {
    title,
    url: url || '',
    style: {
      layout_flexGrow: 1,
      layout_flexBasisPercent: flexBasisMap[num]
    }
  };
}

const result = [];
result.push(createFlexBox("📂 我的收藏", `https://android.jjwxc.net/androidapi/incrementFavorite,{\"method\":\"POST\",\"body\":\"versionCode=313&token={{Map('token')}}&order={\{java.put('order',${order})}}&classId=&backupTime={{Date.now()}}\"}`, "1"));

if(ShelfNum){
const ShelfNumList = String(ShelfNum).split(",").slice(1);
ShelfNumList.forEach(x => {
  const parsedTag = parseTag(ShelfList[x]);
  result.push(createFlexBox(parsedTag[0], parsedTag[1], parsedTag[2],parsedTag[3]));
});
}

authorNames.forEach(tag => {
  const parsedTag = parseTag(tag, tagCategory);
  result.push(createFlexBox(parsedTag[0], parsedTag[1], parsedTag[2],parsedTag[3]));
});

all.forEach(item => {
  const parsedItem = parseCategory(item);
  result.push(createFlexBox(parsedItem[0], parsedItem[1], parsedItem[2]));
});

parseCategoryText(enabledCategories ).forEach(item => {
  const parsedItem = parseCategory(item);
  result.push(createFlexBox(parsedItem[0], parsedItem[1], parsedItem[2]));
});

result.push(createFlexBox(`${标签标识} ${tagName}标签`, null, 1));
tags.forEach(tag => {
  const parsedTag = parseTag(tag, tagCategory);
  result.push(createFlexBox(parsedTag[0], parsedTag[1], parsedTag[2],parsedTag[3]));
});

JSON.stringify(result);

