	function formatDate(datetime) {
    let t = new Date() / 1000;
    var l = t - datetime;
    if (l < 0) {
        return "刚刚";
    }
    var arrayObj = [
                [31536000, "年前"],
                [2592000, "个月前"],
                [604800, "星期前"],
                [86400, "天前"],
                [3600, "小时前"],
                [60, "刚刚"],
                [1, "刚刚"]
            ];
    for (var index in arrayObj) {
        var c = Math.floor(l / arrayObj[index][0]);
        if (0 < c) {
            if ("刚刚" == arrayObj[index][1]) {
                return arrayObj[index][1];
            } else {
                return c + arrayObj[index][1];
            }
        }
    }
