function getloginUrl() {
	let 账号 = result.get("账号");
	let 密码 = result.get("密码");
	let en = encode(密码);
	let id = ("846"+Date.now()+"4893") + ":" + java.androidId() + "d4:";
	let sign = encode(Date.now() + "_" + id + "_");
	let loginUrl = `https://app.jjwxc.org/androidapi/login?versionCode=401&loginName=${encodeURIComponent(账号)}&encode=1&loginPassword=${encodeURIComponent(en)}&sign=${encodeURIComponent(sign)}&identifiers=${encodeURIComponent(id)}&autologin=1`;
	return loginUrl
}

function getCode(t) {
	try {
		let 验证码 = result.get("验证码（填完验证码后，要点👤登录）");
		let 账号 = result.get("账号");
		let 密码 = result.get("密码");
		if (!/^\d{4,}/.test(验证码)) {
			let res = JSON.parse(java.ajax(getloginUrl()));
			if (res.code == "221003") {
				let url = "https://app.jjwxc.org//appDevicesecurityAndroid/getDeviceSecurityCode";
				body = "versionCode=401&username=" + encodeURIComponent(账号) + "&checktype=" + t;
				option = {
					"method": "POST",
					"body": String(body)
				}
				msg = JSON.parse(java.ajax(url + "," + JSON.stringify(option))).data.message;
				java.toast(msg);
			} else {
				msg = /校验验证码/.test(res.message) ? "请请尝试填入上次发给你的验证码（可能是很久之前的）" : res.message;
				msg = msg??"✅登录成功\n❗️请你删除抓包token里的值，以免被旧值覆盖\n🆕最新token值："+res.token+"\n✅无需填验证码";
			   java.log(msg);
			   java.longToast(msg);
			   if (/\d+_[a-z\d]+/.test(String(res.token))) {
					java.log("获取token值， " + res.token);
					LoginToken(String(res.token))
				}
			}
		} else {
			let getCodeurl = getloginUrl() + "&checktype=" + t + "&checkdevicecode=" + 验证码;
			let data = java.ajax(getCodeurl);
			let token = JSON.parse(data).token ?? JSON.parse(data).message;
			java.longToast("✅登录成功\n❗️请你删除抓包token里的值，以免被旧值覆盖\n🆕最新token值："+token);
			java.log("获取到的token值: " + token)
			LoginToken(token)
		}
	} catch (e) {
		java.log(e)
	}
}

function LoginToken(token){
	try{
	token = String(token).replace(/undefined|token=|\s|&.*/g,'');
	token = /^\d+_\w{32}$/.test(token)?token:"";
	if(token==""){
		java.log("设置token失败，请检查填写的token值")
		}

	let fl = Map("fl")==""?"9,10":Map("fl");
	let search = Map("search")==""?"1,8":Map("search");
	let bq = Map("bq")==""?"5":Map("bq");	
	let at = source.getLoginInfoMap().get("作者列表（作者::作者id 注意添加多个用空格分割）");
	at = String((at==""||at==null)?"":at);
	let classNum = Map("classNum");

	setHeaders(
		`token=${token}`,
		`fl=${fl}`,
		`bq=${bq}`,
		`classNum=${classNum}`,
		`at=${java.base64Encode(at)}`,
		`search=${search}`
	)
}catch(e){
	java.log(e);
	}
}

function login(){
	let token = Map("token");
	let t = source.getLoginInfoMap()["抓包token登录[与账号登录二选一,账号登录会让旧token失效,要删除这里]"];		
	if(t!=""&&t!=null)token = t;	
	LoginToken(token);
	}

