let simplifiedToTraditionalMap = {};
let traditionalToSimplifiedMap = {};

for (let i = 0; i < simplifiedChars.length; i++) {
    simplifiedToTraditionalMap[simplifiedChars[i]] = traditionalChars[i];
    traditionalToSimplifiedMap[traditionalChars[i]] = simplifiedChars[i];
}

function toTrad(str) {
    if (typeof str === 'string') {
        let resultArray = [];
        for (let char of str) {
            resultArray.push(simplifiedToTraditionalMap[char] || char);
        }
        return resultArray.join('');
    }
    return str;
}

function toSimp(str) {
    if (typeof str === 'string') {
        let resultArray = [];
        for (let char of str) {
            resultArray.push(traditionalToSimplifiedMap[char] || char);
        }
        return resultArray.join('');
    }
    return str;
}

