jsLib:
function formatDate(dateStr) {
  const date = new Date(dateStr);
  const pad = n => String(n).padStart(2, '0');
  return `${date.getFullYear()}-${pad(date.getMonth()+1)}-${pad(date.getDate())} ` +
         `${pad(date.getHours())}:${pad(date.getMinutes())}:${pad(date.getSeconds())}`;
}
var contentRating = [["普通","safe"],["粗略","suggestive"],["限制","erotica"]];
var sorts = [
    ["最佳匹配", "relevance", "desc"],
    ["最新上传", "latestUploadedChapter", "desc"],
    ["最旧上传", "latestUploadedChapter", "asc"],
    ["标题升序", "title", "asc"],
    ["标题降序", "title", "desc"],
    ["最高评级", "rating", "desc"],
    ["最低评级", "rating", "asc"],
    ["最多关注", "followedCount", "desc"],
    ["最少关注", "followedCount", "asc"],
    ["最近添加", "createdAt", "desc"],
    ["最早添加", "createdAt", "asc"],
    ["年份升序", "year", "asc"],
    ["年份倒序", "year", "desc"]
];

function buildQueryParams(e) {
	const { source } = this
	let selected = JSON.parse(source.getVariable());
  let query = '';
  const allRatings = contentRating.map(([Str,tag]) => tag);

  allRatings.forEach((rating, index) => {
    if (selected.contentRating.includes(rating)) {
      const encoded = `contentRating[]=${rating}`;

      query += `&${encoded}`;
    }
  });
  if (selected.Language) {
    let languages = selected.Language.split(",");
    query += languages.map(item => `&originalLanguage[]=${item}`).join('');
  }
  if (selected.sort && e !== "分类") {
  	let sort_ = selected.sort.split(",");
    query += `&order[${sort_[0]}]=${sort_[1]}`;
    }
  return query;
}

function getLanguage(str) {
    const languageMap = {"en":"英语","en-us":"英语（美国）","en-gb":"英语（英国）","en-au":"英语（澳大利亚）","en-ca":"英语（加拿大）","zh":"中文","zh-tw":"繁体中文（台湾）","zh-hk":"繁体中文（香港）","es":"西班牙语","es-mx":"西班牙语（墨西哥）","es-ar":"西班牙语（阿根廷）","es-es":"西班牙语（西班牙）","fr":"法语","fr-fr":"法语（法国）","fr-ca":"法语（加拿大）","de":"德语","de-de":"德语（德国）","de-at":"德语（奥地利）","ja":"日语","ja-jp":"日语（日本）","ru":"俄语","ru-ru":"俄语（俄罗斯）","pt":"葡萄牙语","pt-br":"葡萄牙语（巴西）","pt-pt":"葡萄牙语（葡萄牙）","it":"意大利语","it-it":"意大利语（意大利）","ko":"韩语","ko-kr":"韩语（韩国）","ar":"阿拉伯语","ar-ae":"阿拉伯语（阿联酋）","ar-sa":"阿拉伯语（沙特）","nl":"荷兰语","nl-nl":"荷兰语（荷兰）","sv":"瑞典语","sv-se":"瑞典语（瑞典）","da":"丹麦语","da-dk":"丹麦语（丹麦）","no":"挪威语","no-no":"挪威语（挪威）","fi":"芬兰语","fi-fi":"芬兰语（芬兰）","tr":"土耳其语","tr-tr":"土耳其语（土耳其）","el":"希腊语","el-gr":"希腊语（希腊）","he":"希伯来语","he-il":"希伯来语（以色列）","pl":"波兰语","pl-pl":"波兰语（波兰）","hu":"匈牙利语","hu-hu":"匈牙利语（匈牙利）","th":"泰语","th-th":"泰语（泰国）","vi":"越南语","vi-vn":"越南语（越南）","id":"印尼语","id-id":"印尼语（印尼）","ms":"马来语","my":"马来语（马来西亚）","ro":"罗马尼亚语","ro-ro":"罗马尼亚语（罗马尼亚）","bg":"保加利亚语","bg-bg":"保加利亚语（保加利亚）","hr":"克罗地亚语","hr-hr":"克罗地亚语（克罗地亚）","cs":"捷克语","cs-cz":"捷克语（捷克）","sk":"斯洛伐克语","sk-sk":"斯洛伐克语（斯洛伐克）","sl":"斯洛文尼亚语","sl-si":"斯洛文尼亚语（斯洛文尼亚）","et":"爱沙尼亚语","et-ee":"爱沙尼亚语（爱沙尼亚）","lv":"拉脱维亚语","lv-lv":"拉脱维亚语（拉脱维亚）","lt":"立陶宛语","lt-lt":"立陶宛语（立陶宛）","uk":"乌克兰语","uk-ua":"乌克兰语（乌克兰）","be":"白俄罗斯语","be-by":"白俄罗斯语（白俄罗斯）"};
    if (languageMap.hasOwnProperty(str)) {
        return languageMap[str];
    }
    const primaryCode = str.split("-")[0];
    return languageMap[primaryCode] || str;
}

