function processPageLines(text) {
  var lines = text.split('\n');
  var pageData = {}; 

  for (var i = 0; i < lines.length; i++) {
    var line = lines[i];
    var match = line.match(/\d这是第(\d+)页的楼层数量：(\d+)/);
    if (match) {
      var page = match[1];
      var count = parseInt(match[2]);     
      if (!pageData[page] || count > pageData[page].maxCount) {
        pageData[page] = {
          maxCount: count,
          bestLine: line,
          originalIndex: i 
        };
      }
    }
  }

  var result = [];
  var usedPages = {}; 
  var pageNumbers = [];


  for (var page in pageData) {
    if (pageData.hasOwnProperty(page)) {
      pageNumbers.push(parseInt(page));
    }
  }


  pageNumbers.sort(function(a, b) {
    return b - a;
  });


  var pagesToKeep = [];
  if (pageNumbers.length >= 3) {

    pagesToKeep.push(pageNumbers[0]);
    pagesToKeep.push(pageNumbers[1]);
  } else {

    pagesToKeep = pageNumbers.slice();
  }


  for (var j = 0; j < lines.length; j++) {
    var currentLine = lines[j];
    var lineMatch = currentLine.match(/\d这是第(\d+)页的楼层数量：(\d+)/);

    if (lineMatch) {
      var currentPage = parseInt(lineMatch[1]);


      var shouldKeep = false;


      if (pageData[currentPage] && pageData[currentPage].bestLine === currentLine) {

        for (var k = 0; k < pagesToKeep.length; k++) {
          if (pagesToKeep[k] === currentPage) {
            shouldKeep = true;
            break;
          }
        }     
        if (shouldKeep && !usedPages[currentPage]) {
          usedPages[currentPage] = true;
          result.push(currentLine);
        }
      }
    } else {  
      result.push(currentLine);
    }
