jsLib:
var headers = {
                    "User-Agent": "okhttp/4.10.0",
                    "n-application-type": "android"
                };
function getMorningDate() {
    const now = new Date();
    const midnight = new Date(now.getFullYear(), now.getMonth(), now.getDate(), 0, 0, 0, 0);
    return Math.floor(midnight.getTime() / 1000);
}

function getUrl() {
	const { java, source } = this
	eval(String(source.loginUrl));
	return String(source.getVariable()).split('|')[0];
}

function generateDateKey() {
    const { java } = this
    const morningTimestamp = Math.floor(getMorningDate() * 1000).toString();
    return java.digestBase64Str(morningTimestamp, "SHA-256");
}

function getSvg(tags) {
	const { java } = this
	let text = "";
	for (let i in tags) {
		text += `<tspan x="0" dy="${i == 0 ? 0 : 12}">${tags[i]}</tspan> `;
		}
 let svg =  `<svg xmlns="http://www.w3.org/2000/svg" 
     viewBox="0 0 655 200" 
     preserveAspectRatio="none"
     style="width: 100%; height: 100%;">
  <rect width="100%" height="100%" fill="white"/>
  <text x="50%" y="30" 
        font-size="20" 
        font-weight="bold" 
        text-anchor="middle" 
        fill="#000">请输入序号</text>
  <text x="50%" y="45" 
        font-size="15" 
        text-anchor="middle" 
        fill="#666">（如1,2,3）</text>
  <text x="0" y="60" 
        font-family="Arial,sans-serif" 
        font-size="12" 
        fill="black">
    ${text}
  </text>
</svg>`;
return `data:image/svg+xml;base64,${java.base64Encode(svg)}`;
}

function getTagUid(key) {
    let { java } = this;
    let tags = key.trim().replace(/^标签@/, "").split(/[,，]/);
    let allTagData = [];

    for (let tag of tags) {
        try {
            let response = java.post(
                `${getUrl.call(this)}/ComicSearch/searchPreViewData/search`,
                `searchContent=${tag.trim()}`, 
                headers
            );

            let searchData = JSON.parse(response.body()).data;
            let tagItems = searchData
                .filter(item => item.hasOwnProperty('uid'))
                .map((item, index) => ({
                    display: `${index + 1}、${item.name}`,
                    uid: item.uid,
                    name: item.name,
                    tag: tag.trim()
                }));

            if (tagItems.length > 0) {
                allTagData.push(tagItems);
            }
        } catch (error) {
            java.toast(`搜索 "${tag.trim()}"标签出错`);
            java.log(error.message);
        }
    }

    if (allTagData.length === 0) {
        java.toast('没有找到匹配的标签');
        return;
    }

    let displayLines = allTagData.map((tagItems, i) => {
        let displayText = tagItems.map(item => item.display).join('; ');
        return `${tags[i].trim()}：${displayText}`;
    });

    let userInput = java.getVerificationCode(getSvg.call(this, displayLines));
    let selectedIndexes = String(userInput).split(/[,，]/);
    let selectedUids = [];

    selectedIndexes.forEach((indexStr, k) => {
        let index = parseInt(indexStr.trim()) - 1;
        if (index >= 0 && index < allTagData[k]?.length) {
            selectedUids.push(allTagData[k][index].uid);
        } else {
            Packages.java.lang.Thread.sleep(50);
            java.toast(`${tags[k]}：序号 ${indexStr} 超出范围\n不进行搜索`);
        }
    });

    if (selectedUids.length > 0) {
        return JSON.stringify(selectedUids);
    } else {
        java.toast('未选取到有效标签');
    }
}

