//构建验证参数
function calculateChecksum(nonce, cookieString) {
    var cpk = "ge_ua_p";
    if (!cookieString || !nonce) {
        return null;
    }

    var cookieValue = "";
    var cookieArray = cookieString.toString().split(";");

    for (var i = 0; i < cookieArray.length; i++) {
        var cookie = cookieArray[i].trim();
        var parts = cookie.split("=");
        if (parts.length === 2 && parts[0] === cpk) {
            cookieValue = parts[1];
            break;
        }
    }

    if (cookieValue.length === 0) {
        return null;
    }

    var sum = 12345;
    for (var i = 0; i < cookieValue.length; i++) {
        var char = cookieValue[i];
        if (/^[a-zA-Z0-9]$/.test(char)) {
            sum += cookieValue.charCodeAt(i) * (nonce + i);
        }
    }

    return {
    	"body": "sum=" + sum + "&nonce=" + nonce,
    	"cookieValue": cookieValue
    	};
}

function Check_(src, cookies = null, url) {
    const { java } = this;
    if (!/Checking your browser/.test(src)) return src;
    java.toast('检测到验证，正在自动处理中……');
    let nonce = parseInt(src.match(/nonce\s*=\s*(\d+)/)[1]);
    cookies = String(cookies);
    //java.log(`${cookies}|${nonce}`);

    let data = calculateChecksum(nonce, cookies);

    let headers = {
        "User-Agent": "Mozilla/5.0 (Android 11; Mobile; rv:140.0) Gecko/140.0 Firefox/140.0",
        "Content-Type": "application/x-www-form-urlencoded",
        "Referer": String(GetUL.call(this) + "/"),
        "X-Ge-Ua-Step": "prev",
        "Cookie": String("ge_ua_p=" + data.cookieValue)
    };

    let postResponse = java.post(GetUL.call(this), data.body, headers);

    let success = JSON.parse(postResponse.body()).ok;
    java.toast(success ? '验证通过' : '验证失败，请重试!');

    if (!success) return src;

    return java.ajax(url);
}

function Sort_(t) {
	   const { source } = this
    const sgvs = JSON.parse(source.getVariable())?.s || 0;

    if (!sgvs) return t === 'search' ? 'relevance' : '';
    const sorts = ['update_time+desc', 'hits+desc', 'word+desc'];
    const sort = sorts[sgvs - 1];

    if (t === 'search') {
        return sort.replace('+', '_');
    }
    return '/order/' + sort;
}

function GetUL() {
	const { source } = this;
	let sourceul = JSON.parse(source.getVariable()).sourl;
	return sourceul;
}

function timeov(times) {
    const date = new Date(times);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    const hours = String(date.getHours()).padStart(2, '0');
    const minutes = String(date.getMinutes()).padStart(2, '0');
    const seconds = String(date.getSeconds()).padStart(2, '0');

    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
}

