}

var sgv = JSON.parse(source.getVariable());
var sg = source.get("list");
var ul = GetUL();

function reg() {
    java.startBrowser(`${ul}/user/user/reg.html`, "注册账号");
}

function Comment() {
    let sgvo = sgv.co;
    sgv.co = sgv.co ? 0 : 1;
    source.setVariable(JSON.stringify(sgv, null, "\t"));
    java.toast(`\n评论区\n【${["开启", "禁用"][sgvo]} → ${["开启", "禁用"][sgv.co]}】`);
}

function Download() {
	   let sgvz = sgv.z;
    sgv.z = sgv.z ? 0 : 1;
    source.setVariable(JSON.stringify(sgv, null, "\t"));
    java.toast(`\n正文限制\n【${["开放", "限制"][sgvz]} → ${["开放", "限制"][sgv.z]}】`);
}

function Updatelog() {
    java.longToast("\n" + java.base64Decode(source.variableComment));
}

function login() {}

function Tgch(o) {
    const sort = ["默认", "更新时间", "人气", "字数"];
    const one_ = sgv.s;
    const msg = (sgv.s = o, source.setVariable(JSON.stringify(sgv, null, "\t")), `排序方式\n【${sort[one_]}→${sort[o]}】`);
    java.toast("\n" + msg);
}

function Geturls() {
    sgv.urls.length = 0;
    try {
        const res = org.jsoup.Jsoup.parse(java.get('https://alicesw.club/', {}).body()).select('.container a');
        const url1 = java.base64Decode(res[0].attr('href').match(/['"](.*?)['"]/)[1]);
        sgv.urls.push(String(url1).replace(/\/?$/, ""));
        const url2 = java.base64Decode(res[1].attr('href').match(/['"](.*?)['"]/)[1]);
        const src = java.get(java.connect(url2).url(), {}).body();
        const seen = new Set();
        for (let match of src.matchAll(/targetHref\:\s*["'](https?[^"']+)\/?["']/g)) {
            if (!seen.has(match[1])) {
                sgv.urls.push(match[1]);
                seen.add(match[1]);
            }
        }
        source.setVariable(JSON.stringify(sgv, null, "\t"));

        if (sgv.urls.length >= 2) {
            java.toast(`获取成功，共获取到${sgv.urls.length}个接口\n┎┅┅┅┅┅┅┅┅┅┅┅┅┅┅┅┒\n${sgv.urls.join('\n')}\n┖┅┅┅┅┅┅┅┅┅┅┅┅┅┅┅┚`);
        } else {
            java.toast('获取失败，请重试或检查网络状况');
        }
    } catch (e) {
        java.log(e);
        java.toast('\n未知错误，请检查网络或更新阅读版本！');
    }
}

function Puturl(e) {
    if (!sgv.urls.length) return java.toast('\n当前没有接口，请获取后选择');
    if (e + 1 > sgv.urls.length) return java.toast(`暂无第${e+1}接口`);

    try {
        const stime = Date.now();
        java.get(sgv.urls[e], {});
        const speedtime = (Date.now() - stime) / 1000;

        sgv.sourl = sgv.urls[e];
        sgv.booklist.forEach(item => {
            item.img = item.img.replace(/https?:\/\/[^\/]+/, sgv.urls[e]);
            item.url = item.url.replace(/https?:\/\/[^\/]+/, sgv.urls[e]);
        });
        source.setVariable(JSON.stringify(sgv, null, "\t"));

        // 切换接口保持登录状态
        const Cookie = cookie.getCookie(sgv.urls[e]);
        if (!/lf_user_auth/.test(Cookie) && source.getLoginHeader()) {
            cookie.setCookie(sgv.urls[e], source.getLoginHeaderMap().get('Cookie'));
        }
        java.toast(`源站已设置为【接口${e + 1}】：\n${sgv.urls[e]}\n♦解析用时${speedtime}秒♦`);
    } catch (err) {
        java.log(err.message);
        java.toast(`\n【接口${e+1}：${sgv.urls[e]}】\n已失效，请检查网络状况或切换其他接口`);
    }
}

function Login() {
    cookie.removeCookie(ul);
    const na = result['用户名/邮箱'];
    const pa = result['密码'];

    if (!na || !pa) return;

    try {
        const phpid = java.get(`${ul}/user/user/verify.html`, {}).header("set-cookies");
        const code = java.getVerificationCode(`${ul}/user/user/verify.html`);
        const response = java.post(`${ul}/user/user/login.html`, 
            `username=${na}&password=${pa}&code=${code}`, 
            { "x-requested-with": "XMLHttpRequest", "Cookie": phpid });

        const ck = cookie.mapToCookie(response.cookies());
        const usercode = JSON.parse(response.body()).msg;
        java.toast(usercode);

        if (ck) {
            source.putLoginHeader(JSON.stringify({"Cookie": ck}));
            cookie.replaceCookie(GetUL(), ck);
        } else {
            source.removeLoginHeader();
            cookie.removeCookie(GetUL());
        }
    } catch (e) {
        java.log(e);
        java.toast('\n登录出错，请切换其他接口重试！');
    }
}

function out() {
    source.removeLoginHeader();
    cookie.removeCookie(GetUL());
    java.toast("已退出登录状态");
}

function show() {
    const jc = source.getLoginHeaderMap();
    let us, id, codes;

    if (jc) {
        const info = decodeURIComponent(decodeURIComponent(jc.get("Cookie")).match(/\{[\s\S]+\}/)[0]);
        const userInfo = JSON.parse(info);
        us = userInfo.username;
        id = userInfo.uid;
        codes = "✔️已登录";
    } else {
        us = id = "无";
        codes = "❌未登录";
    }

    java.longToast(`\n◎用户：${us}◎\n◎用户ID：${id}◎\n◎登录状态：${codes}◎`);
}


function InvitationCode(c) {
    if (!source.getLoginHeaderMap()) return java.toast("未登录，请登录后获取");

    let text = "";
    if (c) {
        java.post(ul + "/user/user/createinvitecode.html", "create_num=1", 
            {"Cookie": source.getLoginHeaderMap()["Cookie"]});
        text += "\n已消耗50积分生成一个邀请码";
    }

    const verify_list = org.jsoup.Jsoup.parse(java.ajax(ul + "/user/user/invitecode")).select('.verify_list .item');
    java.log("\n" + String(verify_list.html()).replace(/<[^>]+?>\s*|复制| (?!\d)/g, ""));
    java.toast(text + "\n邀请码已打印至源日志");
}

function update(u) {
	try {
    const sourcetime = source.lastUpdateTime;
    const upul = "https://fastly.jsdelivr.net/gh/yezechuandl/source-legado@main/source/爱丽丝书屋.json";
    const uptime = JSON.parse(java.get(upul, {}).body())[0].lastUpdateTime;
    if (+uptime > +sourcetime) {
    	   Object.assign(r, sgv);
	       source.setVariable(JSON.stringify(r, null, "\t"));
        java.toast("检测到更新，正在跳转……");
        Packages.java.lang.Thread.sleep(2000);
        java.startBrowser('data:text/html;base64,' + sourceUpdate(sourcetime, uptime), '更新');
    } else if (u != "发现") {
        java.toast(`\n本地书源版本较新，无需更新。\n本地：${timeov(sourcetime)}\n网络：${timeov(uptime)}`);
    }
  } catch (e) {
  	java.log(e.message);
  	java.toast('未知错误');
  }
}

function AFBook(ta) {
    const info = result['书籍ID'];
    if (!info) return java.toast("\n未填入书籍id，请进书籍详情页，或者手动填写。");
    if (!source.getLoginHeaderMap()) return java.toast("未登录，请登录后使用");

    try {
        let url;
        if (ta) {
            url = `${ul}/user/bookshelf/add?novel_id=${info}`;
        } else {
            let src = "";
            for (let page = 1;; page++) {
                let sr = java.ajax(`${ul}/user/bookshelf?page=${page}`);
                if (!/书架空空如也/.test(sr)) {
                    src += sr + "\n";
                } else break;
            }

            const booklist = src.match(/\/novel\/\d+\.html/g);
            const dfid = src.match(/\/user\/bookshelf\/del[^"]+/g);
            const index = booklist.findIndex(book => book.includes(info));
            if (index === -1) return java.toast('未找到书籍');
            url = GetUL() + dfid[index];
        }
        java.log(index + "我的")
        const msg = java.get(url, {"x-requested-with": "XMLHttpRequest"});
        java.toast(JSON.parse(msg.body()).msg);
    } catch (error) {
    	   java.log(error);
        java.toast('未知错误，已打印至日志');
    }
}

function BookShelf() {
    if (source.getLoginHeaderMap()) {
        java.startBrowser(`${ul}/user/bookshelf/`, "书架");
    } else {
        java.toast("未登录，请登录后再使用此功能。");
    }
}

function inBookShelf() {
    java.startBrowser("data:text/html;base64," + booklist(), "书架");
}

function bookinto() {
    if (sg == '') return java.toast('🚫请进入书籍详情页后操作，或者刷新试试');

    const sg1 = JSON.parse(sg);
    const exists = sgv.booklist.some(item => item.ID == sg1.ID);

    if (!exists) {
        sgv.booklist.push(sg1);
        source.setVariable(JSON.stringify(sgv, null, "\t"));
        java.toast("添加成功");
    } else {
        java.toast("书籍已存入本地书架中");
    }
}

function bookmove(a) {
	  if (sg == '') return java.toast('🚫请进入书籍详情页后操作，或者刷新试试');
        if (a == "all") {
            source.put("list", '');
            sgv.booklist.length = 0;
            source.setVariable(JSON.stringify(sgv, null, "\t"));
            java.toast('已清空本地书架');
            return;
        }

        const sg1 = JSON.parse(sg);
        const index = sgv.booklist.findIndex(item => item.ID == sg1.ID);

        if (index != -1) {
            sgv.booklist.splice(index, 1);
            source.setVariable(JSON.stringify(sgv, null, "\t"));
            java.toast(`已从本地删除该书籍。\n书籍ID：${sg1.ID}`);
        } else {
            java.toast('书籍不在本地书架中');
    }
}

