//处理重新获取文本时可能会出现的错误。
let retryCount = 0;
const maxRetries = 3;
let result;
while (retryCount < maxRetries) {
    if (text != "" && !/正在加载小说中,请稍后/.test(text)) {
        result = text;
        break;
    } else {
        retryCount++;
        java.toast(`\n未获取到文本，正在重试...\n（当前第${retryCount} / ${maxRetries}次重试）`);
        text = content();
    }
}
if (retryCount === maxRetries) {
    result = "重试三次仍未获取到文本，请检查网络状况。";
}
result;

