	  let ul = String(t.attr('href'));
	  let url = /\d/.test(ul) ? ul.replace(/\d\.html/, "{{page-1}}.html") : ul + '<,index_{{page}}.html>';
	  push(t.text(), url, 0.45);
});
JSON.stringify(result);

