        returnList.forEach(v => {
            v.title = v.title.replace(RegExp(/^\s+|\s+$/g), "").replace(RegExp(/ï¼ˆ|ï¼‰|-/g), "")
            v.chapterUrl = urlNovel(v.id)
            novelIds.push(v.id)
        })
    } else {
        //é€»è¾‘æ§åˆ¶è€… ä¹Ÿå°±æ˜¯ä½¿ç”¨ä¸Šé¢å®šä¹‰çš„ä¸¤ä¸ªå‡½æ•°æ¥åšå¯¹åº”åŠŸèƒ½
        //è¦çˆ¬å–çš„æ€»æ¬¡æ•°
        let max = (allChaptersCount / limit) + 1
        for (let i = 0; i < max; i++) {
            //java.log("içš„å€¼:"+i)
            let list = sendAjaxForGetChapters(i * limit);
            //å–å‡ºæ¯ä¸ªå€¼
            returnList = returnList.concat(list)
