/**
 * é€šç”¨æ—¥å¿—ä»£ç†åˆ›å»ºå‡½æ•°
 * @param {object} target - è¢«ä»£ç†çš„ç›®æ ‡å¯¹è±¡
 * @param {string} name - åœ¨æ—¥å¿—ä¸­æ˜¾ç¤ºçš„ä»£ç†åç§°
 * @returns {Proxy} - è¿”å›ä¸€ä¸ªå¸¦æ—¥å¿—è®°å½•åŠŸèƒ½çš„ä»£ç†å¯¹è±¡
 */
function createLoggingProxy(target, name) {
  const { java } = this;
  return new Proxy(target, {
    get(target, prop, receiver) {
      //java.log(`${name}`, prop);
      return Reflect.get(target, prop, receiver);
    },
    set(target, prop, value) {
      //java.log(`set ${name}`, prop, value);
      return Reflect.set(target, prop, value);
    },
  });
};

/**
 * æ¨¡æ‹Ÿ ClassList å¯¹è±¡çš„å·¥å‚å‡½æ•°
 * @returns {Proxy}
 */
function createClassList() {
    const classList = {
        add(...args) {
            // java.log("classList add", args);
        },
    };
    return createLoggingProxy(classList, "classList");
};


/**
 * é€šç”¨æ¨¡æ‹Ÿå…ƒç´ æ„é€ å·¥å‚å‡½æ•°
 * @param {string} tagName - å…ƒç´ çš„æ ‡ç­¾å (e.g., 'P', 'STYLE')
 * @param {object} [properties={}] - é™„åŠ åˆ°å…ƒç´ ä¸Šçš„é¢å¤–å±æ€§å’Œæ–¹æ³•
 * @param {string} [logName] - æ—¥å¿—è®°å½•ä¸­ä½¿ç”¨çš„åç§°ï¼Œé»˜è®¤ä¸º tagName çš„å°å†™
 * @returns {Proxy} - è¿”å›ä¸€ä¸ªæ¨¡æ‹Ÿçš„ DOM å…ƒç´ ä»£ç†
 */
function createMockElement(tagName, properties, logName) {
  // æ‰‹åŠ¨å¤„ç†é»˜è®¤å‚æ•°ï¼Œä»¥å…¼å®¹ä¸æ”¯æŒé»˜è®¤å‚æ•°è¯­æ³•çš„æ—§å¼•æ“
  properties = properties || {};

  const upperTagName = tagName.toUpperCase();
  const baseElement = {
    nodeType: 1,
    tagName: upperTagName
  };

  Object.assign(baseElement, properties);

  return createLoggingProxy(baseElement, logName || tagName.toLowerCase());
};

/**
 * ä¸“é—¨ç”¨äºåˆ›å»º P æ ‡ç­¾ä»£ç†çš„å‡½æ•°ï¼Œæ•´åˆäº†æ‰€æœ‰ P æ ‡ç­¾çš„å…±æœ‰è¡Œä¸º
 * @param {string} pOuterHTML - P æ ‡ç­¾çš„ outerHTML å­—ç¬¦ä¸²
 * @returns {Proxy}
 */
function createParagraphProxy(pOuterHTML) {
    const innerHTML = pOuterHTML.replace(/<\/?p>/g, "");
    return createMockElement('P', {
        innerHTML: innerHTML,
        classList: createClassList(),
        get outerHTML() {
            return `<p>${this.innerHTML}</p >`;
        },
    }, 'paragraph');
};

