var cacheSaveSeconds = 7*24*60*60  // ç¼“å­˜æ—¶é—´7å¤©

function cacheGetAndSet(cache, key, supplyFunc) {
    let v = cache.get(key)
    // ç¼“å­˜ä¿¡æ¯é”™è¯¯æ—¶ï¼Œä¿ç•™10minåé‡æ–°è¯·æ±‚
    if (v && JSON.parse(v).error === true) {
        if (new Date().getTime() >= JSON.parse(v).timestamp + 10*60*1000) {
            cache.delete(key)
            v = cache.get(key)
        }
    }
    // æ— ç¼“å­˜ä¿¡æ¯æ—¶ï¼Œè¿›è¡Œè¯·æ±‚
    if (v === undefined || v === null) {
        v = supplyFunc()
        v.timestamp = new Date().getTime()
        v = JSON.stringify(v)
        cache.put(key, v, cacheSaveSeconds)
    }
    return JSON.parse(v)
}

