function sliceCountStr(str) {
   let arr = []; // è®°å½•åˆ†å‰²è®¡ç®—è¡¨è¾¾å¼
   for (let i = 0; i < str.length; i++) {
      let item = str.charAt(i);
      let num = item;
      if (/[\d|\.]/.test(item)) {
          let j = i + 1;
          for (; j < str.length - 1; j++) {
            let otherItem = str.charAt(j);
            if (!/[\d|\.]/.test(otherItem)) {
              break;
            }
          }
          num = str.slice(i, j);
          i = j - 1;
          num = +num;
      }
      arr.push(num);
   }
   return arr;
}
// arr å³ä¸ºä¸Šä¸€æ­¥å¤„ç†å¥½çš„æ•°ç»„
function countHandle(arr) {
      let charArr = [],
        numArr = [];
      for (let i = 0; i < arr.length; i++) {
        if (typeof arr[i] == 'number') {
          numArr.push(arr[i]);
        } else {
          if (charArr.length) {
            // æ­¥éª¤1
            // å¦‚æœå½“å‰çš„è¿ç®—ç¬¦çš„ä¼˜å…ˆçº§æ¯”æ ˆé¡¶çš„ä¼˜å…ˆçº§ä½æˆ–ç›¸ç­‰ï¼Œå°±è¯´æ˜éœ€è¦æŠŠå‰é¢çš„å€¼å…¨éƒ¨è®¡ç®—å¥½
            // å­˜å‚¨è¿ç®—ç¬¦çš„æ ˆè¦ä¸€ç›´å‡ºæ ˆï¼Œç›´åˆ°æ ˆä¸ºç©ºæˆ–å½“å‰çš„å­—ç¬¦çš„ä¼˜å…ˆçº§æ¯”æ ˆé¡¶çš„ä¼˜å…ˆçº§é«˜
            while (this.isPop(arr[i], charArr[charArr.length - 1])) {
              let t2 = numArr.pop();
              let t1 = numArr.pop();
              let char = charArr.pop();
              this.handleCalculation(numArr, t1, t2, char);
            }
            // å½“å‰è¿ç®—ç¬¦ä¸ºå³æ‹¬å·
            if (arr[i] == ')') {
              // å–æ ˆé¡¶è¿ç®—ç¬¦
              let st = charArr[charArr.length - 1];
              // æ­¥éª¤2
              // é‡åˆ°å³æ‹¬å·ä¹Ÿè¦ä¸€ç›´å‡ºæ ˆï¼Œç›´åˆ°é‡åˆ°å·¦æ‹¬å·
              while (st != '(') {
                let t1, t2;
                let char = charArr.pop();
                if (char != '(') {
                  t2 = numArr.pop();
                  t1 = numArr.pop();
                  this.handleCalculation(numArr, t1, t2, char);
                }
                st = char;
              }
            }
            // è¿ç®—ç¬¦ä¸ä¸ºå³æ‹¬å·
            if (arr[i] != ')') {
              charArr.push(arr[i]);
            }
          } else {
            // æ­¥éª¤3
            // è¿ç®—ç¬¦æ ˆä¸ºç©ºï¼Œç›´æ¥å…¥æ ˆ
            charArr.push(arr[i]);
          }
        }
      }
      // æ­¥éª¤4
      // æœ€åè¿ç®—ç¬¦æ ˆå¦‚æœè¿˜æœ‰å­—ç¬¦ï¼Œè¦ä¸€ç›´å‡ºæ ˆç›´åˆ°ä¸ºç©º
      while (charArr.length) {
        let t2 = numArr.pop();
        let t1 = numArr.pop();
        let char = charArr.pop();
        this.handleCalculation(numArr, t1, t2, char);
      }
   return numArr[0];
}
// åŸºæœ¬åŠ å‡ä¹˜é™¤è¿ç®—å¤„ç†
    function handleCalculation(numArr, num1, num2, char) {
      if (char == '+') {
        numArr.push(num1 + num2);
      } else if (char == '-') {
        numArr.push(num1 - num2);
      } else if (char == '*') {
        numArr.push(num1 * num2);
      } else if (char == '/') {
        numArr.push(num1 / num2);
      }
    }
    // åˆ¤æ–­è¿ç®—ç¬¦çš„ä¼˜å…ˆçº§ï¼Œæ˜¯å¦å‡ºæ ˆè¿›è¡Œè®¡ç®—
    function isPop(char1, char2) {
      // è¿ç®—ç¬¦æ ˆä¸ºç©º
      if (!char2) {
        return false;
      }
      // è¿ç®—ç¬¦ä¼˜å…ˆçº§ç›¸åŒ
      if ((char1 == '+' || char1 == '-') && (char2 == '+' || char2 == '-')) {
        return true;
      }
      // å‰è€…è¿ç®—ç¬¦ä¼˜å…ˆçº§æ¯”åè€…ä½
      if ((char1 == '+' || char1 == '-') && (char2 == '*' || char2 == '/')) {
