@js:
let cid = java.hexDecodeToString(result);
let ROUTES = [
    {
        host: "https://fqgo.52dns.cc",
        path: "/content",
        method: "GET",
        priority: 8,
        type:"go"
    },
    {
        host: "https://fqxs.ns114.cc",
        path: "/content",
        method: "GET",
        priority: 8
    },
    {
        host: "https://api.52dns.cc",
        path: "/content",
        method: "GET",
        priority: 8
    }
];
function ssort(r){var t=r.flatMap(r=>Array(Math.max(0,r.priority)).fill(r));for(let r=t.length-1;0<r;r--){var a=Math.floor(Math.random()*(r+1));[t[r],t[a]]=[t[a],t[r]]}return t};
ROUTES = ssort(ROUTES);

function fetchContent(cid) {
    for (let route of ROUTES) {
        java.log(`尝试接口: ${route.host}`);
        try {
            let request = `${route.host}${route.path}?item_id=${cid}`;
            let response = java.get(request, {}).body();
            let result = JSON.parse(response);
            if (result.data.content) {
                java.log("true");
                return result;
            }
        } catch (error) {
            java.log(`接口 ${route.host} 请求失败: ${error}`);
        }
    }
    throw new Error("所有接口均请求失败");
}

function processContent() {
    let content = fetchContent(cid).data.content;

    let md5Regex = /"md5":"([a-f0-9]+)"/g;
    let md5Values = [];
    let match;

    while ((match = md5Regex.exec(content)) !== null) {
        md5Values.push(match[1]);
    }

    if (md5Values.length === 0) {
        return content;
    }

    let imgTags = md5Values.map(md5 => `<img src="https://p6-novel.byteimg.com/origin/novel-pic/${md5}" >`);
    return imgTags.join("");
}

processContent();

