    {name: "人气", key: "weekvisit"},
    {name: "推荐", key: "allvote"},
    {name: "新书", key: "newhot"}
  ];

  const statusTypes = [
    {name: "连载", id: 0},
    {name: "全本", id: 1}
  ];

  let discovery = [];

  // 生成分类发现规则
  categories.forEach(category => {
    // 添加分类标题
    discovery.push({
      title: category.name,
      url: "",
      style: {layout_flexBasisPercent: 1, layout_flexGrow: 1}
    });

    if (category.id === "full" || category.id === "class") {
      // 完本小说和小说分类的子分类
      subCategories.forEach(subCat => {
        discovery.push({
          title: subCat.name,
          url: `/novels/${category.id}/${subCat.id}_{{page}}.html`,
          style: {layout_flexBasisPercent: 0.24, layout_flexGrow: 0.32}
        });
      });

      // 如果是小说分类，后面添加排行榜
      if (category.id === "class") {
        subCategories.forEach(cat => {
          // 添加分类标题
          discovery.push({
            title: `${cat.name}排行榜`,
            url: "",
            style: {layout_flexBasisPercent: 1, layout_flexGrow: 1}
          });

          // 排行榜类型
          statusTypes.forEach(status => {
            rankTypes.forEach(rank => {
              discovery.push({
                title: `${rank.name}-${status.name}`,
                url: `/novels/${rank.key}_${cat.id}_${status.id}_{{page}}.html`,
                style: {layout_flexGrow: 1}
              });
            });
          });
        });
      }
    } else if (category.id === "tag") {
      // 热门标签
      hotTags.forEach(tag => {
        discovery.push({
          title: tag,
          url: `/newtag/${tag}/{{page}}`,
          style: {layout_flexBasisPercent: 0.24, layout_flexGrow: 0.32}
        });
      });
    }
  });

  // 最近更新
  discovery.push({
    title: "最近更新",
    url: "/last",
    style: {layout_flexBasisPercent: 1, layout_flexGrow: 1}
  });

  return JSON.stringify(discovery);
}

