var update = (initUrl, initToken) => {
    try {
        var [novelUrl, chapterId] = initUrl.split('/chapter/');
        var url = novelUrl.replace('/novel/', '/user/read-history/');
        var {Authorization: token} = JSON.parse(initToken);
        return Packages.org.jsoup.Jsoup.connect(url).method(Packages.org.jsoup.Connection.Method.PUT).header("Authorization", token).requestBody(chapterId).execute().body();
    } catch (e) {
        return "";
    }
};
var annotate = (text, dict) => {
    var keys = Object.keys(dict).sort((a, b) => b.length - a.length);
    var pat = new RegExp(keys.join('|'), 'g');
    return text.replace(pat, word => {
        var read = dict[word];
        if (/[ぁ-んァ-ン]/.test(word) && /[一-龯々]/.test(word)) {
            var pre = (word.match(/^([ぁ-んァ-ン]+)/) || [''])[0];
            var suf = (word.match(/([ぁ-んァ-ン]+)$/) || [''])[0];
            var kanji = word.substring(pre.length, word.length - suf.length);
            return (!pre || read.startsWith(pre)) && (!suf || read.endsWith(suf)) ? `${pre}${kanji}(${read.substring(pre.length, read.length - suf.length)})${suf}` : `${word}(${read})`;
        }
        return `${word}(${read})`;
    });
};
var regex = content => {
    var displayedImages = new Set();
    return JSON.stringify(content).replace(/<图片>(https?:\/\/[^"\s]+)/g, (match, url) => {
        if (displayedImages.has(url)) return '';
        displayedImages.add(url);
        var referer = url.match(/https?:\/\/[^\/]+/)[0].replace(/i\.pximg\.net/, 'www.pixiv.net');
        var headers = JSON.stringify({headers: {Referer: referer}});
        return `<img src="${url},${headers}">`;
    });
};
var format = content => content.replace(/^\[|\]$/g, '').split(',').map(item => item.trim().replace(/^"|"$/g, '')).filter(Boolean).map((item, i) => item.startsWith('{"headers"') && i > 0 ? ',' + item : '    ' + item).join("\n");

