jsLib:
function getServerHost() {
	let { source } = this;
    try {
        const config = JSON.parse(source.getVariable());
        return config?.[0]?.host || source.bookSourceUrl;
    } catch(e) {
        return source.bookSourceUrl;
    }
}

function getSessionId() {
    const cookieSessionId = String(this.cookie.getKey("fanqienovel.com", "sessionid"));
    if (cookieSessionId && cookieSessionId !== "null" && cookieSessionId !== "undefined") {
        return cookieSessionId;
    }
    const loginInfo = this.source.getLoginInfoMap();
    if (loginInfo && loginInfo['手动登录Token']) {
        return loginInfo['手动登录Token'];
    }
    return "";
}

function splitArray(input, size) {
  let output = []
  for (let i = 0; i < input.length; i += size) {
    output.push(input.slice(i, i + size))
  }
  return output
}

function getComments(content, bid, cid) {
    let { java, cache, source } = this;
    let host = getServerHost.call(this);
    try {
        let apiUrl = `${host}/proxy_idea?item_id=${cid}`;
        let comments = java.ajax(apiUrl);

        let lines = content.split("\n");
        let raw = JSON.parse(comments).data.data;

        Object.keys(raw).forEach((x) => {
            cache.putMemory(`fq-${bid}-${cid}-${x}-text`, lines[x]);

            let color = "red";
            let loginInfo = source.getLoginInfoMap();
            if (loginInfo && loginInfo['段评图标颜色'] && loginInfo['段评图标颜色'].startsWith('#')) {
                color = loginInfo['段评图标颜色'];
            }

            lines[x] += `<img src="${this.createSvg(raw[x].count, color, bid, cid, x)}">`; });
        return lines.join("\n");
    } catch (e) {
        return content;
    }
}

function createSvg(number, color, bid, cid, para) {
    var displayText = number > 99 ? "99+" : number;
    var date = String(Date.now()).match(/(\d{6}$)/)[1];
    var loginInfoMap = {};
    if (this.source && typeof this.source.getLoginInfoMap == 'function') {
        loginInfoMap = this.source.getLoginInfoMap() || {};
    }
    var bubbleStyle = String(loginInfoMap['段评气泡样式'] || '0');
    var svg;

    if (bubbleStyle == '1') {
        svg = '<svg width="1000" height="833" xmlns="http://www.w3.org/2000/svg">' +
              '<rect x="30" y="20" width="940" height="793" rx="180" ry="180" fill="none" stroke="' + color + '" stroke-width="40" stroke-linejoin="round"/>' +
              '<text x="500" y="444" font-family="Arial, sans-serif" text-anchor="middle" font-size="333" fill="' + color + '" dy="0.3em" font-weight="700" opacity="0.9">' + displayText + '</text>' +
              '</svg>';
    } else if (bubbleStyle == '2') {
        svg = '<svg width="1000" height="1000" xmlns="http://www.w3.org/2000/svg">' +
              '<circle cx="500" cy="500" r="460" fill="none" stroke="' + color + '" stroke-width="40" stroke-linejoin="round"/>' +
              '<text x="500" y="500" font-family="Arial, sans-serif" text-anchor="middle" font-size="360" fill="' + color + '" dy="0.3em" font-weight="700" opacity="0.9">' + displayText + '</text>' +
              '</svg>';
    } else {
        svg = '<svg width="1000" height="909" xmlns="http://www.w3.org/2000/svg">' +
              '<path d="M80,80 h840 a60,60 0 0 1 60,60 v580 a60,60 0 0 1 -60,60 h-620 l-140,90 v-90 h-80 a60,60 0 0 1 -60,-60 v-580 a60,60 0 0 1 60,-60 z" ' +
              'fill="none" stroke="' + color + '" stroke-width="16" stroke-linejoin="round"/>' +
              '<text x="500" y="450" font-family="Arial, sans-serif" text-anchor="middle" ' +
              'font-size="360" fill="' + color + '" dy="0.3em">' + displayText + '</text>' +
              '</svg>';
    }
    var encodedSvg = this.java.base64Encode(svg);
    return 'data:image/svg+xml;base64,' + encodedSvg + ',{\'js\':\'showCmt("' + bid + '","' + cid + '","' + para + '","' + date + '")\'}';
}

function showCmt(bid, cid, para, date) {
    let { java, cache, cookie, source} = this;
    let mname = `fq-${bid}-${cid}-${para}`;
    let host = getServerHost.call(this);
    let load = (cache.getFromMemory(mname) ?? "-").split("-");

    if (load[0] != "1" || load[1] != date) {
        cache.putMemory(mname, "1-" + date);
        return;
    }

    let apiUrl = `${host}/idea_comment?item_id=${cid}&book_id=${bid}&para=${para}&sessionid=${
        (() => {
            let cookieSession = String(cookie.getKey("fanqienovel.com", "sessionid"));
            return cookieSession || (source.getLoginInfoMap())['手动登录Token'];
        })()
    }`;
    let title = cache.getFromMemory(mname + "-text") ?? "段评内容";
    java.startBrowser(apiUrl, title);
}

