function hasValidCustomSource() {
    const value = (source.getLoginInfoMap())['自定义源站'];
    return !(value == null || value == undefined || value == "" || String(value).trim() == "");
}

function showCustomSourceAlert() {
    const customSource = (source.getLoginInfoMap())['自定义源站'];
    java.toast(
        "\n⚠️ 自定义源站配置提醒 ⚠️\n" +
        "──────────────────\n" +
        "检测到已设置自定义源站: \n✨" +
        "【" + (customSource || "") + "】✨\n" +
        "──────────────────\n" +
        "请前往「书源登录」清除配置\n" +
        "才能使指定源站切换生效"
    );
}

function updateConfig(key, value, message, gender) {
    if (key == "source" && hasValidCustomSource()) {
        showCustomSourceAlert();
        return;
    }

    try {
        var config = JSON.parse(source.getVariable());
        if (!config || !Array.isArray(config) || config.length === 0) {
            config = [{}];
        }
        config[0][key] = value;
        if (gender !== undefined) {
            config[0]["gender"] = gender;
        }
        source.setVariable(JSON.stringify(config));
        java.toast(message || "设置已更新");
    } catch(e) {
        var newConfig = {[key]: value};
        if (gender !== undefined) {
            newConfig["gender"] = gender;
        }
        source.setVariable(JSON.stringify([newConfig]));
        java.toast(message || "初始化设置");
    }
}

function get_cx() {
