        if (!config || !Array.isArray(config) || config.length == 0) {
            config = [{host: hosts[0]}]; 
            source.setVariable(JSON.stringify(config)); 
        }
        var currentHost = getServerHost();
        var currentSource = (!('source' in config[0]) || !config[0].source) ? "☁️ 聚合搜索 ☁️" : (sourceList.find(item => item.v === config[0].source)?.m || config[0].source);
        var currentGender = config[0].gender === "boy" ? " 男🧸频 " : (config[0].gender === "girl" ? " 女🎀频 " : " 未设置 ");
