this.sethost = function() {
    try {
        var config = JSON.parse(source.getVariable());
        if (!config || !Array.isArray(config) || config.length == 0) {
            config = [{host: hosts[0]}];
        }

        var currentHost = config[0].host || hosts[0];
        var currentIndex = hosts.indexOf(currentHost);
        var newIndex = (currentIndex + 1) % hosts.length;
        var newHost = hosts[newIndex];
        config[0].host = newHost;
        source.setVariable(JSON.stringify(config));
        java.toast("\n切换到：\n" + newHost + "");
    } catch(e) {
        source.setVariable(JSON.stringify([{host: hosts[0]}]));
        java.toast("【n】已初始化：" + hosts[0]);
