        showCustomSourceAlert();
        return (source.getLoginInfoMap())['自定义源站'];
    }
    var infomap = source.getLoginInfoMap();
    const value = infomap[e];
    if (!value) {
        java.longToast("需要填写密钥");
    }
    return value;
}

function login_(openBrowser, checkMode) {
    var cookie_ = String(cookie.getKey("fanqienovel.com", "sessionid")) || 
                 (source.getLoginInfoMap())['手动登录Token'];

    if (cookie_ && cookie_ != "" && !checkMode) {
        java.toast(source.variableComment ? "请手动移除token后再次登录" : "请不要重复登录，请先退出登录！");
        return false;
    }

    if (openBrowser) {
        try { 	java.startBrowserAwait("https://fanqienovel.com/", "登录");
        } catch (e) {
            java.toast(e);
        }
    }

    cookie.removeCookie("snssdk.com");
    cookie_ = "sessionid=" + (String(cookie.getKey("fanqienovel.com", "sessionid")) || 
    (source.getLoginInfoMap())['手动登录Token']);

    let user;
    try {
        user = JSON.parse(java.ajax("https://fanqienovel.com/api/user/info/v2," + JSON.stringify({
            method: "GET",
            headers: { "Cookie": cookie_ }
        }))).data.name;
    } catch (e) { java.log(e); }

    if (!cookie_ || cookie_ == "sessionid=" || !user) {
        java.toast("未获取到登录凭据，登录失败");
        return false;
    }

    java.toast("欢迎 " + user + "\n登录成功！");
    return true;
}

