function errorReg() {
  return /(java.net.+Exception)|(okhttp.+Exception)|(error code: 1015)|(cf-error[\s\S]*1015)|(503 Service Temporarily Unavailable)|(403 Forbidden)|(Parse error.+in.+wwwroot.+php)|(_cf_chl_opt|challenges.css)|(<center.+>.+不支持)|(章节内容不支持该浏览器显示)|(内容加载失败！)|(客戶端停用中)/gi
}
function ajax(url) {
  const { java, cookie } = this;
  let content = "";
  let time=1;
  	content = java.ajax(url);
  	while (String(content).match(errorReg())) {
    if (time>10) {
  	    java.toast("重试10次后仍请求失败："+url)
  	    throw("重试10次后仍请求失败："+url+content)
  	    break;
    	}
    	if (!!String(content).match(/403|1015/gi)) {
    	  const nowTime = new Date().getTime()
    	  const waitTime = (Math.random() + 1) * time * 10
    	  java.toast(`访问频率过高，等待 ${waitTime} 秒`)
    	  while (new Date().getTime() < nowTime + waitTime * 1000) {}
    	}
    	if (!!String(content).match(/不支持/gi)) {
      //java.log(cookie.getCookie(url));
      cookie.replaceCookie(url,java.webView(null,url,"document.cookie"));
    	}
    	if (!!String(content).match(/_cf_chl_opt|challenges.css|403|客戶端停用中/gi)) {
    		 let i = 1;
      	while (!!String(content).match(/_cf_chl_opt|challenges.css|403|客戶端停用中/gi)) {
	       //java.log('盾');
	       //java.log(content);
        if (i <= 1) {
          content = java.webView(null,url,null);
        } else {
          java.toast("啊哦，静默破盾好像不管用，试试手动过校验吧！");
          content = java.startBrowserAwait(url,"加载完毕后点完成，此页面可能会弹出多次").body();
        }
        i++;
      }
      continue;
    }
  		 java.log("请求失败："+url)
  		 java.log("重试"+time+"次")
  		 let t=new Date().getTime()
    while (new Date().getTime() - t < 500) { }
    if (String(content).match(/1015|503|php/gi)) {
    	  while (new Date().getTime() - t < 5000) { }
    	}
  		 content = java.ajax(url);
  		 time++;
  	}
  	return String(content);
}

/**
 * 通用日志代理创建函数
 * @param {object} target - 被代理的目标对象
 * @param {string} name - 在日志中显示的代理名称
 * @returns {Proxy} - 返回一个带日志记录功能的代理对象
 */
function createLoggingProxy(target, name) {
  const { java } = this;
  return new Proxy(target, {
    get(target, prop, receiver) {
      //java.log(`${name}`, prop);
      return Reflect.get(target, prop, receiver);
    },
    set(target, prop, value) {
      //java.log(`set ${name}`, prop, value);
      return Reflect.set(target, prop, value);
    },
  });
};

/**
 * 模拟 ClassList 对象的工厂函数
 * @returns {Proxy}
 */
function createClassList() {
    const classList = {
        add(...args) {
            // java.log("classList add", args);
        },
    };
    return createLoggingProxy(classList, "classList");
};


/**
 * 通用模拟元素构造工厂函数
 * @param {string} tagName - 元素的标签名 (e.g., 'P', 'STYLE')
 * @param {object} [properties={}] - 附加到元素上的额外属性和方法
 * @param {string} [logName] - 日志记录中使用的名称，默认为 tagName 的小写
 * @returns {Proxy} - 返回一个模拟的 DOM 元素代理
 */
function createMockElement(tagName, properties, logName) {
  // 手动处理默认参数，以兼容不支持默认参数语法的旧引擎
  properties = properties || {};

  const upperTagName = tagName.toUpperCase();
  const baseElement = {
    nodeType: 1,
    tagName: upperTagName
  };

  Object.assign(baseElement, properties);

  return createLoggingProxy(baseElement, logName || tagName.toLowerCase());
};

/**
 * 专门用于创建 P 标签代理的函数，整合了所有 P 标签的共有行为
 * @param {string} pOuterHTML - P 标签的 outerHTML 字符串
 * @returns {Proxy}
 */
function createParagraphProxy(pOuterHTML) {
    const innerHTML = pOuterHTML.replace(/<\/?p>/g, "");
    return createMockElement('P', {
        innerHTML: innerHTML,
        classList: createClassList(),
        get outerHTML() {
            return `<p>${this.innerHTML}</p >`;
        },
    }, 'paragraph');
};

