function login() {
	cookie.removeCookie(source.getKey())
	name = source.getLoginInfoMap().get("用户名");
	word = source.getLoginInfoMap().get("密码");
	if (name&&word){
	ul = "https://m.shencou.com/login.php";
	by = `_17mb_username=${name}&_17mb_password=${word}&ac=login`
	headers = {
		"User-Agent": "Mozilla/5.0 (Linux; Android 9) Mobile Safari/537.36"
		}
	ck = java.post(ul,by,headers).cookies();
	co = String(ck).replace(/\{(.*),(.*)\}/,"$1;$2");
	cookie = {
	"Cookie": co
}
	if (ck) {
		java.toast("用户👤 " + name + " 登录成功");
	source.putLoginHeader(JSON.stringify(cookie));
	} else {
		java.toast("用户👤 " + name + " 登录失败");
		}
}
	}

