@js:
list = [];
list.push({
    text: "💮↓ 试读卷 ↓💮",
    volume: true
});
$ = result;
for (i in $) {
    if (/volume/.test(String($[i]))) {
        text = "🏮↓〖" + String($[i].text()) + "〗↓🏮";
        list.push({
            text: text,
            volume: true
        });
    } else {  
        $[i].select("li").forEach(a => {
            text = a.select("a").text();
            info = String(a.select("a").attr("title")).replace(/更新，共/g, '');
            href = a.select("a").attr("href");
            list.push({   
                text: text,
                info: info,
                href: href
            });
        });
    }
}
list

