if (source.getVariable() == "") {
    java.toast('已初始化');
    let option = {
        contentRating: ['safe', 'suggestive', 'erotica'],
        sort: "",
        Language: ""
    }
    source.setVariable(JSON.stringify(option, null, "\t"));
}
function Login(l) {
	let url = source.key;
	if ( l === "登出") {
	java.toast('请浏览器手动登出');
}
	cookie.removeCookie(url);
	source.removeLoginHeader();
	java.startBrowserAwait(url,"登录",false);
}
var svb = JSON.parse(source.getVariable());
function Show() {
    let text = [];
    const target = svb.contentRating;
    const [sortField, sortOrder] = svb.sort.split(",");
    const languages = svb.Language.split(",").map(x => getLanguage(x)).join(" | ");
    text.push(`\n语言：${languages ? languages : "所有语言"}`);
    const sortKey = sortField || "";
    const sortStr = sorts.find(([_, or, ord]) =>
        sortKey.includes(or) && ord === sortOrder)?. [0] || "默认";
    text.push(`排序方式：${sortStr}`);

    const contentStr = contentRating
        .filter(([_, cr]) => target.includes(cr))
        .map(([str]) => str)
        .join(" | ") || "未设置";
    text.push(`内容等级：${contentStr}`);

    text = text.join("\n");
    java.toast(text);
}

function Put_contentRating(e) {
    let tag = contentRating[e][1];
    let tagStr = contentRating[e][0];
    if (svb.contentRating.includes(tag)) {
        java.toast(`\n已取消展示【${tagStr}级】作品❌`);
        svb.contentRating = svb.contentRating.filter(item => item !== tag);
        java.log(JSON.stringify(svb.contentRating))
    } else {
        java.toast(`\n已添加展示【${tagStr}级】作品✔️`);
        svb.contentRating.push(tag);
    }
    source.setVariable(JSON.stringify(svb, null, "\t"));
}

function Put_Sort(e) {
    if (e !== "默认") {
        sort_ = sorts[e][1] + "," + sorts[e][2];
        point = sorts[e][0];
    } else {
        sort_ = "";
        point = e;
    }
    svb.sort = sort_;
    source.setVariable(JSON.stringify(svb, null, "\t"));
    java.toast(`\n已切换排序为【${point}】`);
}

function login() {
    svb.Language = source.getLoginInfoMap()["语言/缩写（英文逗号分隔多个语言）"].toLowerCase();
    source.setVariable(JSON.stringify(svb, null, "\t"));
}

